typedef websocketpp::server<websocketpp::config::asio> server;

struct connection_data {
    int session_id;
    std::string name;
};

class print_server {
public:
    print_server() : m_next_sessionid(0) {
        m_server.init_asio();
        m_server.set_open_handler(bind(&print_server::on_open,this,_1));
        m_server.set_close_handler(bind(&print_server::on_close,this,_1));
        m_server.set_message_handler(bind(&print_server::on_message,this,_1,_2));
    }

    void on_open(connection_hdl hdl) {
        connection_data data = {m_next_sessionid++, "New User"};
        m_connections.insert(std::make_pair(hdl,data));
        std::cout << "Connection opened, session id: " << data.session_id << std::endl;
    }

    void on_close(connection_hdl hdl) {
        auto it = m_connections.find(hdl);
        if (it != m_connections.end()) {
            std::cout << "Connection closed, session id: " << it->second.session_id << std::endl;
            m_connections.erase(it);
        }
    }

    void on_message(connection_hdl hdl, server::message_ptr msg) {
        connection_data& data = get_data_from_hdl(hdl);
        if (msg->get_payload().substr(0,5) == "/name") {
            data.name = msg->get_payload().substr(6);
            std::cout << "Session " << data.session_id << " renamed to " << data.name << std::endl;
        } else {
            std::cout << "Message from " << data.name << ": " << msg->get_payload() << std::endl;
        }
    }

    connection_data& get_data_from_hdl(connection_hdl hdl) {
        auto it = m_connections.find(hdl);
        if (it == m_connections.end()) {
            throw std::runtime_error("Unknown connection");
        }
        return it->second;
    }

    void run(uint16_t port) {
        m_server.listen(port);
        m_server.start_accept();
        m_server.run();
    }

private:
    int m_next_sessionid;
    server m_server;
    std::map<connection_hdl, connection_data, std::owner_less<connection_hdl>> m_connections;
};

