typedef websocketpp::server<websocketpp::config::asio> server;
typedef server::connection_hdl connection_hdl;

class broadcast_server {
public:
    broadcast_server() : m_server() {
        m_server.init_asio();
        m_server.set_open_handler(bind(&broadcast_server::on_open,this,std::placeholders::_1));
        m_server.set_close_handler(bind(&broadcast_server::on_close,this,std::placeholders::_1));
        m_server.set_message_handler(bind(&broadcast_server::on_message,this,std::placeholders::_1,std::placeholders::_2));
    }

    void run(uint16_t port) {
        m_server.listen(port);
        m_server.start_accept();
        m_server.run();
    }

private:
    enum action_type { SUBSCRIBE, UNSUBSCRIBE, MESSAGE };
    struct action {
        action(action_type t, connection_hdl h = connection_hdl(), server::message_ptr m = server::message_ptr())
            : type(t), hdl(h), msg(m) {}
        action_type type;
        connection_hdl hdl;
        server::message_ptr msg;
    };

    server m_server;
    std::set<connection_hdl> m_connections;
    std::queue<action> m_actions;
    std::mutex m_action_lock;
    std::mutex m_connection_lock;
    std::condition_variable m_action_cond;

    void on_open(connection_hdl hdl) {
        std::lock_guard<std::mutex> guard(m_action_lock);
        m_actions.push(action(SUBSCRIBE, hdl));
        m_action_cond.notify_one();
    }

    void on_close(connection_hdl hdl) {
        std::lock_guard<std::mutex> guard(m_action_lock);
        m_actions.push(action(UNSUBSCRIBE, hdl));
        m_action_cond.notify_one();
    }

    void on_message(connection_hdl hdl, server::message_ptr msg) {
        std::lock_guard<std::mutex> guard(m_action_lock);
        m_actions.push(action(MESSAGE, hdl, msg));
        m_action_cond.notify_one();
    }

    void process_messages() {
        while (true) {
            std::unique_lock<std::mutex> lock(m_action_lock);
            m_action_cond.wait(lock, [&]{ return !m_actions.empty(); });

            action a = m_actions.front();
            m_actions.pop();

            lock.unlock();

            if (a.type == SUBSCRIBE) {
                std::lock_guard<std::mutex> guard(m_connection_lock);
                m_connections.insert(a.hdl);
            } else if (a.type == UNSUBSCRIBE) {
                std::lock_guard<std::mutex> guard(m_connection_lock);
                m_connections.erase(a.hdl);
            } else if (a.type == MESSAGE) {
                std::lock_guard<std::mutex> guard(m_connection_lock);
                for (auto it = m_connections.begin(); it != m_connections.end(); ++it) {
                    m_server.send(*it, a.msg->get_payload(), a.msg->get_opcode());
                }
            }
        }
    }
};

