#include <unistd.h>
#include <stdio.h>
#include <cstring>
#include <cstdlib>

void pipe_communication() {
    int pipe_fd[2];
    pid_t pid;

    char r_buf[4];
    char w_buf[100];
    memset(r_buf,0,sizeof(r_buf));
    memset(w_buf,0,sizeof(w_buf));
    int r_num;

    if(pipe(pipe_fd)<0) {
        printf("pipe create error\n");
        return;
    }

    if((pid=fork())==0) {
        printf("\n");
        close(pipe_fd[1]);
        sleep(5);
        while((r_num=read(pipe_fd[0],r_buf,4))!=0)
            printf("Read data from the pipe is %s\n", r_buf);

        close(pipe_fd[0]);
        exit(0);
    }
    else if(pid>0) {
        close(pipe_fd[0]);
        const char* content = "1111222233334444";
        strncpy(w_buf, content, strlen(content));
        if(write(pipe_fd[1],w_buf, strlen(content))!=-1) {
            printf("Write data %s\n", w_buf);
            printf("Parent write over\n");
        }
        close(pipe_fd[1]);
        printf("parent close fd[1] over\n");
        wait(&pid);
    }
}

