#include <iostream>
#include <unistd.h>
#include <sys/types.h>
#include <cstring>

void pipe_communication() {
    int pipefd[2];
    pid_t pid;
    const char *message = "Hello from parent";

    if (pipe(pipefd) == -1) {
        std::cerr << "pipe create error" << std::endl;
        return;
    }

    pid = fork();
    if (pid == -1) {
        std::cerr << "fork error" << std::endl;
        close(pipefd[0]);
        close(pipefd[1]);
        return;
    }

    if (pid == 0) { // Child process
        close(pipefd[1]); // Close unused write end
        char buffer[100];
        ssize_t bytesRead = read(pipefd[0], buffer, sizeof(buffer) - 1);
        if (bytesRead > 0) {
            buffer[bytesRead] = '\0';
            std::cout << "Read data from the pipe is " << buffer << std::endl;
        }
        close(pipefd[0]);
    } else { // Parent process
        close(pipefd[0]); // Close unused read end
        write(pipefd[1], message, strlen(message));
        std::cout << "Write data " << message << std::endl;
        close(pipefd[1]);
        std::cout << "Parent write over" << std::endl;
        std::cout << "parent close fd[1] over" << std::endl;
    }
}

