#include <sys/socket.h>
#include <netinet/in.h>
#include <arpa/inet.h>
#include <unistd.h>
#include <fcntl.h>
#include <chrono>
#include <thread>
#include <cstring>

class LinHttpHandler {
public:
    class SocketCloser {
    public:
        explicit SocketCloser(int sockFd) : sockFd_(sockFd) {}
        ~SocketCloser() {
            if (sockFd_ != -1) {
                close(sockFd_);
            }
        }
    private:
        int sockFd_;
    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        int sockfd = socket(AF_INET, SOCK_STREAM, 0);
        if (sockfd == -1) {
            throw std::system_error(errno, std::generic_category(), "Socket creation failed");
        }
        SocketCloser closer(sockfd);

        struct sockaddr_in serverAddr;
        memset(&serverAddr, 0, sizeof(serverAddr));
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &serverAddr.sin_addr);

        if (connect(sockfd, reinterpret_cast<struct sockaddr*>(&serverAddr), sizeof(serverAddr)) == -1) {
            throw std::system_error(errno, std::generic_category(), "Connection failed");
        }

        if (send(sockfd, msg.c_str(), msg.size(), 0) == -1) {
            throw std::system_error(errno, std::generic_category(), "Send failed");
        }

        char buffer[4096];
        std::string response;
        ssize_t bytesReceived;

        do {
            bytesReceived = recv(sockfd, buffer, sizeof(buffer), 0);
            if (bytesReceived > 0) {
                response.append(buffer, bytesReceived);
            }
        } while (bytesReceived > 0);

        if (bytesReceived == -1) {
            throw std::system_error(errno, std::generic_category(), "Receive failed");
        }

        return response;
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sockfd = socket(AF_INET, SOCK_DGRAM, 0);
        if (sockfd == -1) {
            throw std::system_error(errno, std::generic_category(), "Socket creation failed");
        }
        SocketCloser closer(sockfd);

        struct sockaddr_in multicastAddr;
        memset(&multicastAddr, 0, sizeof(multicastAddr));
        multicastAddr.sin_family = AF_INET;
        multicastAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &multicastAddr.sin_addr);

        if (setsockopt(sockfd, SOL_SOCKET, SO_REUSEADDR, &int{1}, sizeof(int)) == -1) {
            throw std::system_error(errno, std::generic_category(), "Setsockopt failed");
        }

        if (bind(sockfd, reinterpret_cast<struct sockaddr*>(&multicastAddr), sizeof(multicastAddr)) == -1) {
            throw std::system_error(errno, std::generic_category(), "Bind failed");
        }

        if (sendto(sockfd, msg.c_str(), msg.size(), 0, reinterpret_cast<struct sockaddr*>(&multicastAddr), sizeof(multicastAddr)) == -1) {
            throw std::system_error(errno, std::generic_category(), "Sendto failed");
        }

        std::vector<std::string> responses;
        std::chrono::steady_clock::time_point endTime = std::chrono::steady_clock::now() + timeout;
        fd_set readfds;
        struct timeval tv;

        while (std::chrono::steady_clock::now() < endTime) {
            FD_ZERO(&readfds);
            FD_SET(sockfd, &readfds);
            tv.tv_sec = 0;
            tv.tv_usec = 100000;

            int activity = select(sockfd + 1, &readfds, nullptr, nullptr, &tv);
            if (activity < 0) {
                throw std::system_error(errno, std::generic_category(), "Select failed");
            }

            if (FD_ISSET(sockfd, &readfds)) {
                char buffer[4096];
                struct sockaddr_in clientAddr;
                socklen_t addrLen = sizeof(clientAddr);
                ssize_t bytesReceived = recvfrom(sockfd, buffer, sizeof(buffer), 0, reinterpret_cast<struct sockaddr*>(&clientAddr), &addrLen);
                if (bytesReceived > 0) {
                    responses.emplace_back(buffer, bytesReceived);
                }
            }
        }

        return responses;
    }
};

