class TCPClient {
public:
    TCPClient(const std::string& address, u16 port) : address_(address), port_(port), main_socket_(-1), connected_(false) {}

    bool Connect() {
        main_socket_ = socket(AF_INET, SOCK_STREAM, 0);
        if (main_socket_ == -1) {
            return false;
        }

        struct sockaddr_in server_addr;
        memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(port_);

        if (inet_pton(AF_INET, address_.c_str(), &server_addr.sin_addr) <= 0) {
            close(main_socket_);
            return false;
        }

        int flags = fcntl(main_socket_, F_GETFL, 0);
        fcntl(main_socket_, F_SETFL, flags | O_NONBLOCK);

        if (ConnectWithTimeout(main_socket_, &server_addr, sizeof(server_addr), 5) == false) {
            close(main_socket_);
            return false;
        }

        connected_ = true;
        return true;
    }

    bool Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected_) {
            return false;
        }

        ssize_t total_bytes_read = 0;
        auto start_time = std::chrono::high_resolution_clock::now();

        while (total_bytes_read < static_cast<ssize_t>(size)) {
            ssize_t bytes_read = recv(main_socket_, static_cast<char*>(buffer) + total_bytes_read, size - total_bytes_read, 0);
            if (bytes_read > 0) {
                total_bytes_read += bytes_read;
            } else if (bytes_read == 0) {
                // Connection closed by server
                Disconnect();
                return false;
            } else {
                // Check for timeout
                auto now = std::chrono::high_resolution_clock::now();
                if (std::chrono::duration_cast<std::chrono::nanoseconds>(now - start_time) >= timeout) {
                    return false;
                }
                std::this_thread::sleep_for(std::chrono::milliseconds(1));
            }
        }

        return true;
    }

    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected_) {
            return false;
        }

        ssize_t total_bytes_written = 0;
        auto start_time = std::chrono::high_resolution_clock::now();

        while (total_bytes_written < static_cast<ssize_t>(size)) {
            ssize_t bytes_written = send(main_socket_, static_cast<const char*>(buffer) + total_bytes_written, size - total_bytes_written, 0);
            if (bytes_written > 0) {
                total_bytes_written += bytes_written;
            } else if (bytes_written == 0) {
                // Connection closed by server
                Disconnect();
                return false;
            } else {
                // Check for timeout
                auto now = std::chrono::high_resolution_clock::now();
                if (std::chrono::duration_cast<std::chrono::nanoseconds>(now - start_time) >= timeout) {
                    return false;
                }
                std::this_thread::sleep_for(std::chrono::milliseconds(1));
            }
        }

        return true;
    }

    void Disconnect() {
        if (connected_) {
            close(main_socket_);
            main_socket_ = -1;
            connected_ = false;
        }
    }

    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) {
            return false;
        }

        if (nonBlocking) {
            flags |= O_NONBLOCK;
        } else {
            flags &= ~O_NONBLOCK;
        }

        if (fcntl(sockFD, F_SETFL, flags) == -1) {
            return false;
        }

        return true;
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        if (SetNonBlock(sockFD, true) == false) {
            return false;
        }

        if (connect(sockFD, reinterpret_cast<struct sockaddr*>(server_addr), server_addr_len) < 0) {
            if (errno != EINPROGRESS) {
                return false;
            }
        }

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);

        struct timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        int result = select(static_cast<int>(sockFD) + 1, nullptr, &write_fds, nullptr, &tv);
        if (result <= 0) {
            return false;
        }

        int so_error = 0;
        socklen_t len = sizeof(so_error);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &so_error, &len) < 0) {
            return false;
        }

        if (so_error != 0) {
            return false;
        }

        return true;
    }

private:
    std::string address_;
    u16 port_;
    SocketHolder main_socket_;
    bool connected_;
};

