#include <iostream>
#include <string>
#include <unordered_map>
#include <mutex>
#include <thread>
#include <atomic>
#include <condition_variable>
#include <sys/socket.h>
#include <sys/un.h>
#include <unistd.h>
#include <fcntl.h>
#include <vector>
#include <memory>

class Stats {
public:
    static Stats& get(const std::string& stats_socket_path) {
        static std::unique_ptr<Stats> instance;
        static std::once_flag once_flag;
        std::call_once(once_flag, [&stats_socket_path]() {
            instance = std::make_unique<Stats>(stats_socket_path);
        });
        return *instance;
    }

    static std::unique_ptr<Stats> get_for_unittest(const std::string& stats_socket_path) {
        return std::make_unique<Stats>(stats_socket_path);
    }

    static bool init(const std::string& stats_socket_path) {
        try {
            auto instance = std::make_unique<Stats>(stats_socket_path);
            if (!instance->startSocket()) {
                return false;
            }
            return true;
        } catch (...) {
            return false;
        }
    }

    static bool isInit() {
        static bool initialized = false;
        return initialized;
    }

    std::unordered_map<std::string, int> getAll() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_;
    }

    int increment(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        auto it = stats_.find(key);
        if (it != stats_.end()) {
            it->second += val;
        } else {
            stats_[key] = val;
        }
        return stats_[key];
    }

    int set(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] = val;
        return stats_[key];
    }

    int reset() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_.clear();
        return 0;
    }

    ~Stats() {
        if (sockfd_ >= 0) {
            close(sockfd_);
        }
        if (!stats_socket_path_.empty()) {
            unlink(stats_socket_path_.c_str());
        }
        if (stats_thread_.joinable()) {
            statsThreadRunning_ = false;
            thread_exited_.notify_all();
            stats_thread_.join();
        }
    }

private:
    Stats(const std::string& stats_socket_path) : stats_socket_path_(stats_socket_path), statsThreadRunning_(true) {
        if (stats_socket_path_.empty()) {
            throw std::invalid_argument("Socket path cannot be empty");
        }
        unlink(stats_socket_path_.c_str());
        sockfd_ = socket(AF_UNIX, SOCK_STREAM, 0);
        if (sockfd_ < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to create socket");
        }
        serv_addr_.sun_family = AF_UNIX;
        strncpy(serv_addr_.sun_path, stats_socket_path_.c_str(), sizeof(serv_addr_.sun_path) - 1);
        if (bind(sockfd_, reinterpret_cast<struct sockaddr*>(&serv_addr_), sizeof(serv_addr_)) < 0) {
            close(sockfd_);
            throw std::system_error(errno, std::generic_category(), "Failed to bind socket");
        }
        if (listen(sockfd_, 5) < 0) {
            close(sockfd_);
            throw std::system_error(errno, std::generic_category(), "Failed to listen on socket");
        }
        stats_thread_ = std::thread(&Stats::runSocket, this);
    }

    bool startSocket() {
        statsThreadRunning_ = true;
        stats_thread_ = std::thread(&Stats::runSocket, this);
        return true;
    }

    void runSocket() {
        while (statsThreadRunning_) {
            struct sockaddr_un cli_addr;
            socklen_t cli_len = sizeof(cli_addr);
            int client_sockfd = accept(sockfd_, reinterpret_cast<struct sockaddr*>(&cli_addr), &cli_len);
            if (client_sockfd < 0) {
                if (errno == EINTR) continue;
                break;
            }

            std::lock_guard<std::mutex> lock(thread_mutex_);
            ++thread_count_;
            std::thread([this, client_sockfd]() {
                processMsg(client_sockfd);
                std::lock_guard<std::mutex> lock(thread_mutex_);
                --thread_count_;
                thread_exited_.notify_one();
            }).detach();
        }
    }

    void processMsg(int sockfd) {
        char buffer[1024];
        ssize_t bytes_read = read(sockfd, buffer, sizeof(buffer) - 1);
        if (bytes_read <= 0) {
            close(sockfd);
            return;
        }
        buffer[bytes_read] = '\0';
        std::string request(buffer);

        std::string response;
        if (request.find("get ") == 0) {
            std::string key = request.substr(4);
            std::lock_guard<std::mutex> lock(stats_mutex_);
            auto it = stats_.find(key);
            if (it != stats_.end()) {
                response = std::to_string(it->second);
            } else {
                response = "0";
            }
        } else if (request == "reset") {
            std::lock_guard<std::mutex> lock(stats_mutex_);
            stats_.clear();
            response = "OK";
        } else if (request.find("set ") == 0) {
            size_t space_pos = request.find(' ', 4);
            if (space_pos != std::string::npos) {
                std::string key = request.substr(4, space_pos - 4);
                int value = std::stoi(request.substr(space_pos + 1));
                std::lock_guard<std::mutex> lock(stats_mutex_);
                stats_[key] = value;
                response = "OK";
            }
        } else if (request.find("increment ") == 0) {
            size_t space_pos = request.find(' ', 10);
            if (space_pos != std::string::npos) {
                std::string key = request.substr(10, space_pos - 10);
                int value = std::stoi(request.substr(space_pos + 1));
                std::lock_guard<std::mutex> lock(stats_mutex_);
                auto it = stats_.find(key);
                if (it != stats_.end()) {
                    it->second += value;
                } else {
                    stats_[key] = value;
                }
                response = "OK";
            }
        } else {
            response = "Unknown command";
        }

        write(sockfd, response.c_str(), response.size());
        close(sockfd);
