typedef boost::shared_ptr<udp::socket> udp_socket_ptr;
typedef boost::shared_ptr<boost::asio::steady_timer> timer_ptr;
typedef boost::shared_ptr<std::string> control_request_ptr;

class server {
public:
  server(boost::asio::io_context& io_context, unsigned short port)
    : acceptor_(io_context, tcp::endpoint(tcp::v4(), port)),
      timer_(io_context),
      udp_socket_(io_context),
      next_frame_number_(0) {
    start_accept();
    timer_.expires_from_now(boost::posix_time::seconds(1));
    timer_.async_wait(boost::bind(&server::handle_timer, this));
  }

private:
  tcp::acceptor acceptor_;
  boost::asio::steady_timer timer_;
  udp::socket udp_socket_;
  unsigned long next_frame_number_;
  std::set<udp::endpoint> subscribers_;

  void start_accept() {
    tcp_socket_ptr socket(new tcp::socket(acceptor_.get_executor()));
    acceptor_.async_accept(*socket, boost::bind(&server::handle_accept, this, _1, socket));
  }

  void handle_accept(const error_code& ec, tcp_socket_ptr socket) {
    if (!ec) {
      control_request_ptr request(new std::string);
      socket->async_read_some(boost::asio::buffer(*request), 
        boost::bind(&server::handle_control_request, this, _1, socket, request));
    }
    start_accept();
  }

  void handle_control_request(const error_code& ec, tcp_socket_ptr socket, control_request_ptr request) {
    if (!ec) {
      std::string control_data = *request;
      if (control_data == "subscribe") {
        udp::endpoint endpoint;
        socket->remote_endpoint(endpoint);
        subscribers_.insert(endpoint);
        std::cout << "Subscribed client: " << endpoint << std::endl;
      } else if (control_data == "unsubscribe") {
        udp::endpoint endpoint;
        socket->remote_endpoint(endpoint);
        subscribers_.erase(endpoint);
        std::cout << "Unsubscribed client: " << endpoint << std::endl;
      }
      // Handle other control requests as needed
    }
  }

  void handle_control_request_timer(tcp_socket_ptr socket, control_request_ptr request, timer_ptr delay_timer) {
    delay_timer->cancel();
    handle_control_request(error_code(), socket, request);
  }

  void handle_timer() {
    timer_.expires_from_now(boost::posix_time::seconds(1));
    timer_.async_wait(boost::bind(&server::handle_timer, this));

    if (!subscribers_.empty()) {
      std::ostringstream frame;
      frame << "Frame #" << next_frame_number_++ << " Broadcast";
      std::string data = frame.str();
      for (const auto& endpoint : subscribers_) {
        udp_socket_.send_to(boost::asio::buffer(data), endpoint);
      }
    }
  }
