class DownloadTask {
public:
    static void initialize() {
        curl_global_init(CURL_GLOBAL_DEFAULT);
    }

    static void uninitialize() {
        curl_global_cleanup();
    }

    DownloadTask(const std::string& url, const std::string& output, const std::string& defaultFile, time_t mtime, const std::string& name = "")
        : m_url(url), m_output(output), m_default(defaultFile), m_mtime(mtime), m_name(name), m_retries(0), m_userAgent("Mozilla/5.0") {
        m_outputTmp = m_output + ".tmp";
    }

    bool run() {
        if (!downloadFile(m_url)) {
            if (!downloadFile(m_urlBackup)) {
                return false;
            }
        }
        return true;
    }

    unsigned int getRetries() const {
        return m_retries;
    }

    std::string getUrl() const {
        return m_url;
    }

    std::string getName() const {
        return m_name;
    }

    std::string getError() const {
        return m_error;
    }

private:
    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;

    size_t writeData(void* ptr, size_t size, size_t nmemb) {
        std::ofstream file(m_outputTmp, std::ios::binary | std::ios::app);
        if (!file.is_open()) {
            m_error = "Failed to open temporary file for writing.";
            return 0;
        }
        file.write(static_cast<char*>(ptr), size * nmemb);
        file.close();
        return size * nmemb;
    }

    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            m_error = "Failed to initialize CURL.";
            return false;
        }

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &DownloadTask::writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, this);
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
        curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);
        curl_easy_setopt(curl, CURLOPT_TIMEOUT, 30L);

        CURLcode res = curl_easy_perform(curl);
        curl_easy_cleanup(curl);

        if (res != CURLE_OK) {
            m_error = "CURL error: " + std::string(curl_easy_strerror(res));
            m_retries++;
            return false;
        }

        if (rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
            m_error = "Failed to rename temporary file to final destination.";
            return false;
        }

        struct stat st;
        if (stat(m_output.c_str(), &st) == 0) {
            st.st_mtime = m_mtime;
            if (utime(m_output.c_str(), &st) != 0) {
                m_error = "Failed to set modification time on downloaded file.";
                return false;
            }
        }

        return true;
    }
};

