class HttpWebRequest {
public:
    std::string uri_;
    std::string method_;
    std::map<std::string, std::string> header_;
    std::iostream is_;

    HttpWebRequest(std::string &uri) : uri_(uri), method_("GET") {}

    static size_t callback(void *buffer, size_t size, size_t nmemb, void *data) {
        size_t realsize = size * nmemb;
        ((std::string*)data)->append((char*)buffer, realsize);
        return realsize;
    }

    HttpWebResponse* GetFileResponse(const char *uri) {
        HttpWebResponse* response = new HttpWebResponse();
        std::ifstream file(uri, std::ios::in | std::ios::binary);
        if (file.is_open()) {
            file.seekg(0, std::ios::end);
            response->content.resize(file.tellg());
            file.seekg(0, std::ios::beg);
            file.read(&response->content[0], response->content.size());
            file.close();
        } else {
            response->content = "Failed to open file";
        }
        return response;
    }

    HttpWebResponse* GetNetResponse(const char *uri) {
        HttpWebResponse* response = new HttpWebResponse();
        CURL *curl;
        CURLcode res;

        curl_global_init(CURL_GLOBAL_DEFAULT);
        curl = curl_easy_init();

        if (curl) {
            curl_easy_setopt(curl, CURLOPT_URL, uri);
            curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, callback);
            curl_easy_setopt(curl, CURLOPT_WRITEDATA, &response->content);
            curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);

            res = curl_easy_perform(curl);
            if (res != CURLE_OK) {
                response->content = "Error: " + std::string(curl_easy_strerror(res));
            }

            curl_easy_cleanup(curl);
        }

        curl_global_cleanup();
        return response;
    }

    HttpWebResponse* GetResponse() {
        if (uri_.find("file://") == 0) {
            return GetFileResponse(uri_.c_str());
        } else {
            return GetNetResponse(uri_.c_str());
        }
    }
};

