void pipe_communication() {
    int pipe_fd[2];
    if (pipe(pipe_fd) == -1) {
        std::cerr << "pipe create error" << std::endl;
        return;
    }

    pid_t pid = fork();
    if (pid < 0) {
        std::cerr << "fork error" << std::endl;
        close(pipe_fd[0]);
        close(pipe_fd[1]);
        return;
    }

    if (pid == 0) {
        // Child process
        close(pipe_fd[1]);

        char buffer[100];
        ssize_t bytes_read = read(pipe_fd[0], buffer, sizeof(buffer));
        if (bytes_read > 0) {
            buffer[bytes_read] = '\0';
            std::cout << "Read data from the pipe is " << buffer << std::endl;
        }

        close(pipe_fd[0]);
    } else {
        // Parent process
        close(pipe_fd[0]);

        const char* message = "Hello from parent";
        std::cout << "Write data " << message << std::endl;
        write(pipe_fd[1], message, strlen(message) + 1);
        std::cout << "Parent write over" << std::endl;

        close(pipe_fd[1]);
        std::cout << "parent close fd[1] over" << std::endl;
    }
}

