  struct chat_message {
    enum { header_length = 4 };
    enum { max_body_length = 512 };

    chat_message() : body_length_(0) {}
    explicit chat_message(const std::string& body)
      : body_length_(body.size())
    {
      std::memcpy(body_.data(), body.c_str(), body.size());
    }

    const char* data() const { return data_; }
    std::size_t length() const { return header_length + body_length_; }
    const char* body() const { return body_.data(); }
    std::size_t body_length() const { return body_length_; }
    void body_length(std::size_t new_length) { body_length_ = new_length; }
    void encode_header()
    {
      std::memcpy(header_.data(), &body_length_, header_length);
    }

  private:
    char header_[header_length];
    char body_[max_body_length];
    std::size_t body_length_;
  };

  class posix_chat_client
    : public boost::enable_shared_from_this<posix_chat_client> {
  public:
    posix_chat_client(asio::io_context& io_context,
                      const tcp::resolver::results_type& endpoints)
      : socket_(io_context), input_(io_context, ::dup(STDIN_FILENO)),
        output_(io_context, ::dup(STDOUT_FILENO)), read_msg_(),
        write_msg_(), input_buffer_() {
      do_connect(endpoints);
    }

    void do_connect(const tcp::resolver::results_type& endpoints) {
      socket_.async_connect(endpoints,
        [this](const asio::error_code& error) {
          if (!error) {
