            do_read_input();
          }
        });
    }

    void do_read_header() {
      asio::async_read(socket_, asio::buffer(read_msg_.header_),
        [this](const asio::error_code& error, std::size_t) {
          if (!error) {
            read_msg_.body_length_ = 
              *reinterpret_cast<const std::size_t*>(read_msg_.header_);
            do_read_body();
          }
        });
    }

    void do_read_body() {
      asio::async_read(socket_, asio::buffer(read_msg_.body_, read_msg_.body_length_),
        [this](const asio::error_code& error, std::size_t) {
          if (!error) {
            do_write_output();
            do_read_header();
          }
