        [this](const asio::error_code& error, std::size_t) {
          if (!error) {
            // No action needed on completion
          }
        });
    }

    void do_read_input() {
      asio::async_read_until(input_, input_buffer_, '\n',
        [this](const asio::error_code& error, std::size_t) {
