#include <vector>

namespace net = boost::asio;
namespace ssl = net::ssl;
namespace beast = boost::beast;
namespace websocket = beast::websocket;
namespace tcp = net::ip::tcp;

class Device {
public:
    std::string host;
    int port;
};

class Logging {
public:
    void log(const std::string& message) {
        // Implementation for logging
    }
};

class WebOsClient {
public:
    class Impl {
    public:
        Impl(net::io_context& ioc, ssl::context& ctx, Device& device_settings, std::shared_ptr<Logging> log)
            : ioc_(ioc), ctx_(ctx), device_settings_(device_settings), log_(log), resolver_(ioc_), buffer_(8192), host_(""), socket_status_(0) {}

        void run() {
            host_ = device_settings_.host + ":" + std::to_string(device_settings_.port);
            resolver_.async_resolve(host_, "ws", [this](beast::error_code ec, tcp::resolver::results_type results) {
                onResolve(ec, results);
            });
        }

        void onResolve(beast::error_code ec, tcp::resolver::results_type results) {
            if (ec) {
                onError(ec, "Hostname resolution failed");
                return;
            }

            auto endpoint = results.begin()->endpoint();
            if (device_settings_.host.find("https") != std::string::npos) {
                ws_.emplace(ioc_, ctx_);
                ws_->next_layer().lowest_layer().connect(endpoint, [this](beast::error_code ec) {
                    onConnect(ec, endpoint);
                });
            } else {
                ws_tcp_.emplace(ioc_);
                ws_tcp_->next_layer().connect(endpoint, [this](beast::error_code ec) {
                    onConnect(ec, endpoint);
                });
            }
        }

        void onConnect(beast::error_code ec, tcp::resolver::results_type::endpoint_type endpoint) {
            if (ec) {
                onError(ec, "TCP connection failed");
                return;
            }

            if (device_settings_.host.find("https") != std::string::npos) {
                ws_->next_layer().handshake(ssl::stream_base::client, [this](beast::error_code ec) {
                    onSSLhandshake(ec);
                });
            } else {
                ws_tcp_->handshake(websocket::stream_base::client, [this](beast::error_code ec) {
                    onWinsockHandshake(ec);
                });
            }
        }

        void onSSLhandshake(beast::error_code ec) {
            if (ec) {
                onError(ec, "SSL handshake failed");
                return;
            }

            ws_->handshake(host_, "/webos", [this](beast::error_code ec) {
                onWinsockHandshake(ec);
            });
        }

        void onWinsockHandshake(beast::error_code ec) {
            if (ec) {
                onError(ec, "WebSocket handshake failed");
                return;
            }

            socket_status_ = 1;
            read();
        }

        void send(const std::string& payload, const std::string& type) {
            if (!is_connected()) return;

            beast::flat_buffer buffer;
            beast::ostream(buffer) << type << payload;
            ws_->text(true);
            ws_->async_write(buffer.data(), [this](beast::error_code ec, std::size_t) {
                onWrite(ec, 0);
            });
        }

        void onWrite(beast::error_code ec, std::size_t) {
            if (ec) {
                onError(ec, "Write operation failed");
            }
        }

        void read() {
            if (!is_connected()) return;

            ws_->async_read(buffer_, [this](beast::error_code ec, std::size_t) {
                onRead(ec, 0);
            });
        }

        void onRead(beast::error_code ec, std::size_t) {
            if (ec) {
                onError(ec, "Read operation failed");
                return;
            }

            // Handle received data
            read();
        }

        void onError(beast::error_code& ec, const std::string& message) {
            log_->log("Error: " + message + " - " + ec.message());
            close();
        }

        void enqueueWork(Work&) {
            // Implementation for work queue
        }

        void close() {
            if (is_connected()) {
                ws_->close(websocket::close_code::normal);
                socket_status_ = 0;
            }
        }

        bool is_connected() {
            return socket_status_ == 1;
        }

    private:
        net::io_context& ioc_;
        ssl::context& ctx_;
        Device& device_settings_;
        std::shared_ptr<Logging> log_;
        tcp::resolver resolver_;
        std::optional<websocket::stream<beast::ssl_stream<beast::tcp_stream>>> ws_;
        std::optional<websocket::stream<beast::tcp_stream>> ws_tcp_;
        beast::flat_buffer buffer_;
        std::string host_;
        int socket_status_;
    };
};
