#include <sys/types.h>
#include <sys/socket.h>
#include <netinet/in.h>
#include <arpa/inet.h>
#include <unistd.h>
#include <netdb.h>
#include <cstring>
#include <string>
#include <vector>
#include <stdexcept>
#include <system_error>
#include <chrono>

class LinHttpHandler {
private:
    class SocketCloser {
        int fd;
    public:
        explicit SocketCloser(int sockFd) : fd(sockFd) {}
        ~SocketCloser() { if (fd >= 0) close(fd); }
        SocketCloser(const SocketCloser&) = delete;
        SocketCloser& operator=(const SocketCloser&) = delete;
    };

public:
    std::string send(const std::string& msg, const std::string& adr, int port) {
        struct addrinfo hints, *res;
        memset(&hints, 0, sizeof hints);
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_STREAM;

        std::string port_str = std::to_string(port);
        int status = getaddrinfo(adr.c_str(), port_str.c_str(), &hints, &res);
        if (status != 0) {
            throw std::system_error(status, std::generic_category(), "getaddrinfo failed");
        }

        int sockfd = socket(res->ai_family, res->ai_socktype, res->ai_protocol);
        if (sockfd == -1) {
            freeaddrinfo(res);
            throw std::system_error(errno, std::system_category(), "socket failed");
        }

        SocketCloser closer(sockfd);

        if (connect(sockfd, res->ai_addr, res->ai_addrlen) == -1) {
            freeaddrinfo(res);
            throw std::system_error(errno, std::system_category(), "connect failed");
        }

        freeaddrinfo(res);

        ssize_t total_sent = 0;
        while (total_sent < static_cast<ssize_t>(msg.size())) {
            ssize_t sent = send(sockfd, msg.data() + total_sent, msg.size() - total_sent, 0);
            if (sent == -1) {
                throw std::system_error(errno, std::system_category(), "send failed");
            }
            total_sent += sent;
        }

        std::string response;
        char buffer[4096];
        while (true) {
            ssize_t bytes_received = recv(sockfd, buffer, sizeof(buffer), 0);
            if (bytes_received == -1) {
                throw std::system_error(errno, std::system_category(), "recv failed");
            }
            if (bytes_received == 0) {
                break;
            }
            response.append(buffer, bytes_received);
        }

        return response;
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sockfd = socket(AF_INET, SOCK_DGRAM, 0);
        if (sockfd == -1) {
            throw std::system_error(errno, std::system_category(), "socket failed");
        }

        SocketCloser closer(sockfd);

        struct sockaddr_in local_addr;
        memset(&local_addr, 0, sizeof(local_addr));
        local_addr.sin_family = AF_INET;
        local_addr.sin_addr.s_addr = htonl(INADDR_ANY);
        local_addr.sin_port = htons(0);

        if (bind(sockfd, (struct sockaddr*)&local_addr, sizeof(local_addr)) == -1) {
            throw std::system_error(errno, std::system_category(), "bind failed");
        }

        int ttl = 1;
        if (setsockopt(sockfd, IPPROTO_IP, IP_MULTICAST_TTL, &ttl, sizeof(ttl)) == -1) {
            throw std::system_error(errno, std::system_category(), "setsockopt IP_MULTICAST_TTL failed");
        }

        struct sockaddr_in dest_addr;
        memset(&dest_addr, 0, sizeof(dest_addr));
        dest_addr.sin_family = AF_INET;
        dest_addr.sin_port = htons(port);
        if (inet_pton(AF_INET, adr.c_str(), &dest_addr.sin_addr) <= 0) {
            throw std::system_error(EINVAL, std::generic_category(), "invalid multicast address");
        }

        if (sendto(sockfd, msg.data(), msg.size(), 0, (struct sockaddr*)&dest_addr, sizeof(dest_addr)) == -1) {
            throw std::system_error(errno, std::system_category(), "sendto failed");
        }

        auto timeout_ms = std::chrono::duration_cast<std::chrono::milliseconds>(timeout).count();
        timeval tv;
        tv.tv_sec = static_cast<long>(timeout_ms / 1000);
        tv.tv_usec = static_cast<decltype(tv.tv_usec)>((timeout_ms % 1000) * 1000);

        if (setsockopt(sockfd, SOL_SOCKET, SO_RCVTIMEO, &tv, sizeof(tv)) == -1) {
            throw std::system_error(errno, std::system_category(), "setsockopt SO_RCVTIMEO failed");
        }

        std::vector<std::string> responses;
        char buffer[65536];
        while (true) {
            sockaddr_in sender_addr;
            socklen_t sender_len = sizeof(sender_addr);
            ssize_t len = recvfrom(sockfd, buffer, sizeof(buffer), 0, (struct sockaddr*)&sender_addr, &sender_len);
            if (len == -1) {
                if (errno == EAGAIN || errno == EWOULDBLOCK) {
                    break;
                }
                throw std::system_error(errno, std::system_category(), "recvfrom failed");
            }
            if (len > 0) {
                responses.emplace_back(buffer, len);
            }
        }

        return responses;
    }
};

