ClientSocket::ClientSocket(ThreadCtx& threadCtx, const string& dest, int port, const EncryptionParams& encryptionParams, int64_t ivChangeInterval)
    : dest_(dest), threadCtx_(threadCtx), sa_(nullptr), socket_(nullptr) {
    struct addrinfo hints = {};
    memset(&hints, 0, sizeof(hints));
    hints.ai_family = AF_UNSPEC;
    hints.ai_socktype = SOCK_STREAM;
    hints.ai_protocol = IPPROTO_TCP;
    char portStr[6];
    snprintf(portStr, sizeof(portStr), "%d", port);
    int status = getaddrinfo(dest.c_str(), portStr, &hints, &sa_);
    if (status != 0) {
        sa_ = nullptr;
    }
}

ClientSocket::~ClientSocket() {
    closeConnection();
    if (sa_) {
        freeaddrinfo(sa_);
    }
}

ErrorCode ClientSocket::connect() {
    if (!sa_) {
        return ERROR_CODE_ADDR_INFO;
    }
    for (struct addrinfo* p = sa_; p != nullptr; p = p->ai_next) {
        socket_ = make_unique<WdtSocket>(p->ai_family, p->ai_socktype, p->ai_protocol);
        if (!socket_ || socket_->getFd() == -1) {
            continue;
        }
        setSendBufferSize();
        socket_->setNonBlocking(true);
        int connRes = socket_->connect(p->ai_addr, p->ai_addrlen);
        if (connRes < 0) {
            if (errno == EINPROGRESS) {
                int timeoutMs = threadCtx_.getConnectTimeout();
                struct pollfd pfd = {socket_->getFd(), POLLOUT, 0};
                int pollRes = poll(&pfd, 1, timeoutMs);
                if (pollRes <= 0) {
                    continue;
                }
                int so_error;
                socklen_t len = sizeof(so_error);
                getsockopt(socket_->getFd(), SOL_SOCKET, SO_ERROR, &so_error, &len);
                if (so_error != 0) {
                    continue;
                }
            } else {
                continue;
            }
        }
        socket_->setNonBlocking(false);
        struct sockaddr_storage addr;
        socklen_t addrLen = sizeof(addr);
        if (getpeername(socket_->getFd(), reinterpret_cast<sockaddr*>(&addr), &addrLen) == 0) {
            char ip[INET6_ADDRSTRLEN];
            if (addr.ss_family == AF_INET) {
                inet_ntop(AF_INET, &((struct sockaddr_in*)&addr)->sin_addr, ip, INET_ADDRSTRLEN);
            } else {
                inet_ntop(AF_INET6, &((struct sockaddr_in6*)&addr)->sin6_addr, ip, INET6_ADDRSTRLEN);
            }
            peerIp_ = ip;
        }
        return ERROR_CODE_SUCCESS;
    }
    return ERROR_CODE_CONNECT_FAILED;
}

void ClientSocket::setSendBufferSize() {
    int size = threadCtx_.getOptions().sendBufferSize_;
    if (size > 0) {
        socket_->setSendBufferSize(size);
    }
}

void ClientSocket::closeConnection() {
    socket_.reset();
}

const string& ClientSocket::getPeerIp() const {
    return peerIp_;
}

