#include <time.h>

struct stEndPoint {
    char* ip;
    unsigned short port;
};

void* readwrite_routine(void* arg) {
    struct stEndPoint* endpoint = (struct stEndPoint*)arg;
    const char* message = "Fixed message";
    const size_t message_len = strlen(message);

    while (1) {
        int sockfd = socket(AF_INET, SOCK_STREAM, 0);
        if (sockfd < 0) {
            sleep(1);
            continue;
        }

        int flags = fcntl(sockfd, F_GETFL, 0);
        fcntl(sockfd, F_SETFL, flags | O_NONBLOCK);

        struct sockaddr_in serverAddr;
        memset(&serverAddr, 0, sizeof(serverAddr));
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(endpoint->port);

        if (endpoint->ip == NULL || inet_pton(AF_INET, endpoint->ip, &serverAddr.sin_addr) <= 0) {
            inet_pton(AF_INET, "127.0.0.1", &serverAddr.sin_addr);
        }

        int conn_res = connect(sockfd, (struct sockaddr*)&serverAddr, sizeof(serverAddr));
        if (conn_res < 0) {
            if (errno != EINPROGRESS) {
                close(sockfd);
                sleep(1);
                continue;
            }

            fd_set write_fds;
            FD_ZERO(&write_fds);
            FD_SET(sockfd, &write_fds);
            struct timeval timeout = {5, 0};
            int select_res = select(sockfd + 1, NULL, &write_fds, NULL, &timeout);
            if (select_res <= 0) {
                close(sockfd);
                sleep(1);
                continue;
            }

            int error;
            socklen_t len = sizeof(error);
            getsockopt(sockfd, SOL_SOCKET, SO_ERROR, &error, &len);
            if (error != 0) {
                close(sockfd);
                sleep(1);
                continue;
            }
        }

        flags = fcntl(sockfd, F_GETFL, 0);
        fcntl(sockfd, F_SETFL, flags & ~O_NONBLOCK);

        struct timeval tv = {5, 0};
        setsockopt(sockfd, SOL_SOCKET, SO_SNDTIMEO, (const void*)&tv, sizeof(struct timeval));
        setsockopt(sockfd, SOL_SOCKET, SO_RCVTIMEO, (const void*)&tv, sizeof(struct timeval));

        int connected = 1;
        while (connected) {
            ssize_t sent = send(sockfd, message, message_len, 0);
            if (sent <= 0) {
                connected = 0;
                continue;
            }

            char buffer[1500];
            ssize_t recv_len = recv(sockfd, buffer, sizeof(buffer) - 1, 0);
            if (recv_len <= 0) {
                connected = 0;
                continue;
            }
            buffer[recv_len] = '\0';
        }

        close(sockfd);
        sleep(1);
    }

    return NULL;
}

