#include <iostream>
#include <sys/socket.h>
#include <netinet/in.h>
#include <unistd.h>
#include <signal.h>
#include <sys/wait.h>
#include <cstdlib>
#include <cerrno>
#include <cstring>

void tcp_server_communication() {
    struct sigaction sa;
    sa.sa_handler = [](int signal) {
        while (waitpid(-1, nullptr, WNOHANG) > 0);
    };
    sa.sa_flags = SA_RESTART;
    sigemptyset(&sa.sa_mask);
    sigaction(SIGCHLD, &sa, nullptr);

    int server_fd = socket(AF_INET, SOCK_STREAM, 0);
    if (server_fd == -1) {
        return;
    }

    sockaddr_in address{};
    address.sin_family = AF_INET;
    address.sin_addr.s_addr = INADDR_ANY;
    address.sin_port = htons(9736);

    if (bind(server_fd, reinterpret_cast<sockaddr*>(&address), sizeof(address)) == -1) {
        close(server_fd);
        return;
    }

    if (listen(server_fd, 5) == -1) {
        close(server_fd);
        return;
    }

    while (true) {
        int client_fd = accept(server_fd, nullptr, nullptr);
        if (client_fd == -1) {
            continue;
        }

        pid_t pid = fork();
        if (pid == 0) {
            close(server_fd);
            char c;
            if (read(client_fd, &c, 1) == 1) {
                sleep(1);
                c++;
                write(client_fd, &c, 1);
            }
            close(client_fd);
            exit(0);
        } else if (pid > 0) {
            close(client_fd);
        }
    }

    close(server_fd);
}

