    void processMsg(int sockfd);
private:
    static bool& isInitInternal();
    std::string stats_socket_path_;
    int sockfd_;
    struct sockaddr_un serv_addr_;
    std::thread stats_thread_;
    std::atomic<bool> statsThreadRunning_;
    std::mutex stats_mutex_;
    std::mutex thread_mutex_;
    std::condition_variable thread_exited_;
    int thread_count_;
    std::unordered_map<std::string, int> stats_;
    static std::unique_ptr<Stats> instance_;
    static std::mutex init_mutex_;
};
std::unique_ptr<Stats> Stats::instance_ = nullptr;
std::mutex Stats::init_mutex_;
bool& Stats::isInitInternal() {
    static bool initialized = false;
    return initialized;
}
bool Stats::isInit() {
    return isInitInternal();
}
Stats::Stats(const std::string& stats_socket_path) 
    : stats_socket_path_(stats_socket_path), sockfd_(-1), 
      statsThreadRunning_(false), thread_count_(0) {
    memset(&serv_addr_, 0, sizeof(serv_addr_));
}
Stats::~Stats() {
    statsThreadRunning_ = false;
    if (sockfd_ != -1) {
        close(sockfd_);
        sockfd_ = -1;
    }
    if (stats_thread_.joinable()) {
        stats_thread_.join();
    }
    {
        std::unique_lock<std::mutex> lock(thread_mutex_);
        while (thread_count_ > 0) {
            thread_exited_.wait(lock);
        }
    }
    unlink(stats_socket_path_.c_str());
}
Stats& Stats::get(const std::string& stats_socket_path) {
    std::lock_guard<std::mutex> lock(init_mutex_);
    if (!instance_) {
        instance_ = std::make_unique<Stats>(stats_socket_path);
        instance_->startSocket();
    }
    return *instance_;
}
std::unique_ptr<Stats> Stats::get_for_unittest(const std::string& stats_socket_path) {
    return std::make_unique<Stats>(stats_socket_path);
}
bool Stats::init(const std::string& stats_socket_path) {
    std::lock_guard<std::mutex> lock(init_mutex_);
    if (instance_) {
        return false;
    }
    instance_ = std::make_unique<Stats>(stats_socket_path);
    isInitInternal() = true;
    return instance_->startSocket();
}
std::unordered_map<std::string, int> Stats::getAll() {
    std::lock_guard<std::mutex> lock(stats_mutex_);
    return stats_;
}
int Stats::increment(const std::string& key, int val) {
    std::lock_guard<std::mutex> lock(stats_mutex_);
    stats_[key] += val;
    return stats_[key];
}
int Stats::set(const std::string& key, int val) {
    std::lock_guard<std::mutex> lock(stats_mutex_);
    stats_[key] = val;
    return stats_[key];
}
int Stats::reset() {
    std::lock_guard<std::mutex> lock(stats_mutex_);
    stats_.clear();
    return 0;
}
bool Stats::startSocket() {
    sockfd_ = socket(AF_UNIX, SOCK_STREAM, 0);
    if (sockfd_ == -1) {
        return false;
    }
    unlink(stats_socket_path_.c_str());
    serv_addr_.sun_family = AF_UNIX;
    strncpy(serv_addr_.sun_path, stats_socket_path_.c_str(), sizeof(serv_addr_.sun_path)-1);
    if (bind(sockfd_, (struct sockaddr*)&serv_addr_, sizeof(serv_addr_)) == -1) {
        close(sockfd_);
        sockfd_ = -1;
        return false;
    }
    if (listen(sockfd_, 5) == -1) {
        close(sockfd_);
        sockfd_ = -1;
        return false;
    }
    statsThreadRunning_ = true;
    stats_thread_ = std::thread(&Stats::runSocket, this);
    return true;
}
void Stats::runSocket() {
    while (statsThreadRunning_) {
        struct sockaddr_un cli_addr;
        socklen_t clilen = sizeof(cli_addr);
        int newsockfd = accept(sockfd_, (struct sockaddr*)&cli_addr, &clilen);
        if (newsockfd == -1) {
            continue;
        }
        {
            std::lock_guard<std::mutex> lock(thread_mutex_);
            thread_count_++;
        }
        std::thread([this, newsockfd]() {
            processMsg(newsockfd);
            {
                std::lock_guard<std::mutex> lock(thread_mutex_);
                thread_count_--;
                if (thread_count_ == 0) {
                    thread_exited_.notify_all();
                }
            }
            close(newsockfd);
        }).detach();
    }
}
void Stats::processMsg(int sockfd) {
    char buffer[1024];
    memset(buffer, 0, sizeof(buffer));
    int n = read(sockfd, buffer, sizeof(buffer)-1);
    if (n <= 0) {
        return;
    }
    std::string request(buffer);
    std::istringstream iss(request);
    std::string command;
    iss >> command;
    std::string response;
    if (command == "GETALL") {
        auto data = getAll();
        for (const auto& pair : data) {
            response += pair.first + ":" + std::to_string(pair.second) + "\n";
        }
    } else if (command == "GET") {
        std::string key;
        iss >> key;
        std::lock_guard<std::mutex> lock(stats_mutex_);
        auto it = stats_.find(key);
        if (it != stats_.end()) {
            response = std::to_string(it->second);
        } else {
            response = "0";
        }
    } else if (command == "RESET") {
        reset();
        response = "OK";
    } else if (command == "INCREMENT") {
        std::string key;
        int val = 0;
        iss >> key >> val;
        int new_val = increment(key, val);
        response = std::to_string(new_val);
    } else if (command == "SET") {
        std::string key;
        int val = 0;
        iss >> key >> val;
        int new_val = set(key, val);
        response = std::to_string(new_val);
    } else {
        response = "UNKNOWN_COMMAND";
    }
    write(sockfd, response.c_str(), response.size());
}

