class DFNetWork;

class UDPRecv {
public:
    UDPRecv(DFNetWork* poDFNetWork) : m_poDFNetWork(poDFNetWork), m_iSockFD(-1), m_bIsEnd(false), m_bIsStarted(false) {}
    ~UDPRecv() { Stop(); if (m_iSockFD != -1) close(m_iSockFD); }
    void Stop() { m_bIsEnd = true; if (m_thread.joinable()) m_thread.join(); m_bIsStarted = false; }
    int Init(int iPort) {
        m_iSockFD = socket(AF_INET, SOCK_DGRAM, 0);
        if (m_iSockFD == -1) return -1;
        int flags = fcntl(m_iSockFD, F_GETFL, 0);
        if (fcntl(m_iSockFD, F_SETFL, flags | O_NONBLOCK) == -1) {
            close(m_iSockFD);
            m_iSockFD = -1;
            return -1;
        }
        struct sockaddr_in addr;
        std::memset(&addr, 0, sizeof(addr));
        addr.sin_family = AF_INET;
        addr.sin_port = htons(iPort);
        addr.sin_addr.s_addr = INADDR_ANY;
        if (bind(m_iSockFD, (struct sockaddr*)&addr, sizeof(addr)) == -1) {
            close(m_iSockFD);
            m_iSockFD = -1;
            return -1;
        }
        return 0;
    }
    void run() {
        fd_set read_fds;
        struct timeval timeout;
        char buffer[65536];
        while (!m_bIsEnd) {
            FD_ZERO(&read_fds);
            FD_SET(m_iSockFD, &read_fds);
            timeout.tv_sec = 0;
            timeout.tv_usec = 100000;
            int ret = select(m_iSockFD + 1, &read_fds, nullptr, nullptr, &timeout);
            if (ret < 0) continue;
            else if (ret == 0) continue;
            if (FD_ISSET(m_iSockFD, &read_fds)) {
                struct sockaddr_in senderAddr;
                socklen_t senderAddrLen = sizeof(senderAddr);
                ssize_t recvLen = recvfrom(m_iSockFD, buffer, sizeof(buffer), 0, (struct sockaddr*)&senderAddr, &senderAddrLen);
                if (recvLen > 0) m_poDFNetWork->ProcessUDPMessage(buffer, recvLen, &senderAddr);
            }
        }
    }
    bool Start() {
        if (m_bIsStarted) return false;
        m_bIsStarted = true;
        m_bIsEnd = false;
        m_thread = std::thread(&UDPRecv::run, this);
        return true;
    }
private:
    DFNetWork* m_poDFNetWork;
    int m_iSockFD;
    std::atomic<bool> m_bIsEnd;
    bool m_bIsStarted;
    std::thread m_thread;
};

