namespace asio = boost::asio;
using tcp = asio::ip::tcp;
using udp = asio::ip::udp;

class server {
public:
    using tcp_socket_ptr = std::shared_ptr<tcp::socket>;
    using control_request_ptr = std::shared_ptr<std::vector<char>>;
    using timer_ptr = std::shared_ptr<asio::steady_timer>;

    server(asio::io_context& io_context, unsigned short port)
        : acceptor_(io_context, tcp::endpoint(tcp::v4(), port)),
          udp_socket_(io_context, udp::endpoint(udp::v4(), port)),
          timer_(io_context),
          next_frame_number_(0) {
        acceptor_.listen();
        start_accept();
        timer_.expires_from_now(boost::posix_time::seconds(1));
        timer_.async_wait([this](const boost::system::error_code&) { handle_timer(); });
    }

private:
    void start_accept() {
        auto socket = std::make_shared<tcp::socket>(acceptor_.get_executor().context());
        acceptor_.async_accept(*socket, [this, socket](const boost::system::error_code& ec) {
            handle_accept(ec, socket);
        });
    }

    void handle_accept(const boost::system::error_code& ec, tcp_socket_ptr socket) {
        if (!ec) {
            auto request = std::make_shared<std::vector<char>>(1024);
            socket->async_read_some(asio::buffer(*request), [this, socket, request](const boost::system::error_code& ec, std::size_t bytes) {
                if (!ec) request->resize(bytes);
                handle_control_request(ec, socket, request);
            });
        }
        start_accept();
    }

    void handle_control_request(const boost::system::error_code& ec, tcp_socket_ptr socket, control_request_ptr request) {
        if (!ec) {
            auto delay_timer = std::make_shared<asio::steady_timer>(acceptor_.get_executor().context());
            delay_timer->expires_from_now(boost::posix_time::seconds(0));
            delay_timer->async_wait([this, socket, request, delay_timer](const boost::system::error_code&) {
                handle_control_request_timer(socket, request, delay_timer);
            });
        }
    }

    void handle_control_request_timer(tcp_socket_ptr socket, control_request_ptr request, timer_ptr) {
        std::string req_data(request->begin(), request->end());
        if (req_data.find("SUBSCRIBE") != std::string::npos) {
            udp::endpoint ep(socket->remote_endpoint().address(), socket->remote_endpoint().port());
            subscribers_.insert(ep);
        } else if (req_data.find("UNSUBSCRIBE") != std::string::npos) {
            udp::endpoint ep(socket->remote_endpoint().address(), socket->remote_endpoint().port());
            subscribers_.erase(ep);
        }
        boost::system::error_code ignored;
        socket->shutdown(tcp::socket::shutdown_both, ignored);
        socket->close(ignored);
    }

    void handle_timer() {
        std::vector<char> frame_data;
        frame_data.reserve(32);
        std::string frame_str = "FRAME:" + std::to_string(next_frame_number_++);
        std::copy(frame_str.begin(), frame_str.end(), std::back_inserter(frame_data));
        for (const auto& ep : subscribers_) {
            udp_socket_.async_send_to(asio::buffer(frame_data), ep, [](const boost::system::error_code&, std::size_t) {});
        }
        timer_.expires_from_now(boost::posix_time::seconds(1));
        timer_.async_wait([this](const boost::system::error_code&) { handle_timer(); });
    }

    tcp::acceptor acceptor_;
    udp::socket udp_socket_;
    asio::steady_timer timer_;
    unsigned long next_frame_number_;
    std::set<udp::endpoint> subscribers_;
};

