size_t writeTaskHttpData(void *buffer, size_t size, size_t nmemb, void *user_p)
{
    DownloadTask *task = reinterpret_cast<DownloadTask *>(user_p);
    if (NULL != task)
    {
        return task->writeData(buffer, size, nmemb);
    }

    return 0;
