#include <curl/curl.h>
#include <string>
#include <cstdio>
#include <sys/stat.h>
#include <utime.h>
#include <unistd.h>

class DownloadTask {
public:
    static void initialize() { curl_global_init(CURL_GLOBAL_DEFAULT); }
    static void uninitialize() { curl_global_cleanup(); }

    DownloadTask(const std::string& url, const std::string& output, const std::string& defaultFile, time_t mtime, const std::string& name)
        : m_url(url), m_urlBackup(""), m_output(output), m_outputTmp(output + ".tmp"), m_default(defaultFile), m_mtime(mtime), m_retries(0), m_name(name), m_userAgent("DownloadTask/1.0") {}

    bool run() {
        bool success = false;
        for (unsigned int i = 0; i < 3; ++i) {
            m_retries++;
            if (downloadFile(m_url)) {
                success = true;
                break;
            }
        }
        if (!success && !m_urlBackup.empty()) {
            m_retries++;
            if (downloadFile(m_urlBackup)) {
                success = true;
            }
        }
        if (!success && !m_default.empty()) {
            if (copyFile(m_default, m_output)) {
                success = true;
            }
        }
        return success;
    }

    unsigned int getRetries() const { return m_retries; }

    static size_t writeData(void* contents, size_t size, size_t nmemb, void* userp) {
        return static_cast<DownloadTask*>(userp)->writeDataInternal(contents, size, nmemb);
    }

    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            m_error = "Failed to initialize CURL";
            return false;
        }

        m_fileFP = fopen(m_outputTmp.c_str(), "wb");
        if (!m_fileFP) {
            m_error = "Failed to open temporary file";
            curl_easy_cleanup(curl);
            return false;
        }

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, this);
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
        curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);

        CURLcode res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            m_error = "CURL error: " + std::string(curl_easy_strerror(res));
            fclose(m_fileFP);
            remove(m_outputTmp.c_str());
            curl_easy_cleanup(curl);
            return false;
        }

        long http_code = 0;
        curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &http_code);
        if (http_code >= 400) {
            m_error = "HTTP error: " + std::to_string(http_code);
            fclose(m_fileFP);
            remove(m_outputTmp.c_str());
            curl_easy_cleanup(curl);
            return false;
        }

        fclose(m_fileFP);
        curl_easy_cleanup(curl);

        if (rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
            m_error = "Failed to rename temporary file";
            remove(m_outputTmp.c_str());
            return false;
        }

        struct utimbuf ut;
        ut.actime = m_mtime;
        ut.modtime = m_mtime;
        utime(m_output.c_str(), &ut);

        return true;
    }

private:
    bool copyFile(const std::string& src, const std::string& dst) {
        FILE* in = fopen(src.c_str(), "rb");
        if (!in) return false;
        FILE* out = fopen(dst.c_str(), "wb");
        if (!out) { fclose(in); return false; }

        char buffer[4096];
        size_t bytes;
        while ((bytes = fread(buffer, 1, sizeof(buffer), in)) > 0) {
            if (fwrite(buffer, 1, bytes, out) != bytes) {
                fclose(out); fclose(in); remove(dst.c_str());
                return false;
            }
        }

        fclose(out); fclose(in);
        return true;
    }

    size_t writeDataInternal(void* contents, size_t size, size_t nmemb) {
        size_t written = fwrite(contents, size, nmemb, m_fileFP);
        return written * size;
    }

private:
    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;
    FILE* m_fileFP;
};

