    std::string m_output;
    Task(const std::string& url, const std::string& output) : m_url(url), m_output(output) {}
    void run();
    static size_t writeData(void* buffer, size_t size, size_t nmemb, void* userp);
};

class DownloadPool {
private:
    std::vector<std::thread> m_threads;
    std::queue<Task> m_queue;
    std::unordered_set<std::string> m_urls;
    std::mutex m_mtx;
    std::condition_variable m_cond;
    bool m_noMoreTask;

    void run() {
        while (true) {
            std::unique_lock<std::mutex> lock(m_mtx);
            m_cond.wait(lock, [this] { return !m_queue.empty() || m_noMoreTask; });
            if (m_noMoreTask && m_queue.empty()) {
                return;
            }
            if (m_queue.empty()) {
                continue;
            }
            Task task = std::move(m_queue.front());
            m_queue.pop();
            lock.unlock();
            task.run();
        }
    }

public:
    DownloadPool() : m_noMoreTask(false) {
        curl_global_init(CURL_GLOBAL_DEFAULT);
        int numThreads = std::thread::hardware_concurrency();
        if (numThreads == 0) numThreads = 4;
        for (int i = 0; i < numThreads; ++i) {
            m_threads.emplace_back([this]() { this->run(); });
        }
    }

    ~DownloadPool() {
        setNoMoreTask();
        finishAndWaitForExit();
        curl_global_cleanup();
    }

    void addTask(const std::string& url, const std::string& output) {
        std::lock_guard<std::mutex> lock(m_mtx);
        if (m_urls.find(url) != m_urls.end()) {
            return;
        }
        m_urls.insert(url);
        m_queue.push(Task(url, output));
        m_cond.notify_one();
    }

    void setNoMoreTask() {
        std::lock_guard<std::mutex> lock(m_mtx);
        m_noMoreTask = true;
        m_cond.notify_all();
    }

    void finishAndWaitForExit() {
        for (auto& t : m_threads) {
            if (t.joinable()) {
                t.join();
            }
        }
    }
};

void Task::run() {
    FILE* fp = fopen(m_output.c_str(), "wb");
    if (!fp) return;
    CURL* curl = curl_easy_init();
    if (!curl) {
        fclose(fp);
        return;
    }
    curl_easy_setopt(curl, CURLOPT_URL, m_url.c_str());
    curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &Task::writeData);
    curl_easy_setopt(curl, CURLOPT_WRITEDATA, fp);
    curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);
    curl_easy_perform(curl);
    curl_easy_cleanup(curl);
    fclose(fp);
}

size_t Task::writeData(void* buffer, size_t size, size_t nmemb, void* userp) {
    FILE* fp = static_cast<FILE*>(userp);
    return fwrite(buffer, size, nmemb, fp);
}

