        } else {
            ws_tcp_->async_write(net::buffer(data),
                beast::bind_front_handler(&Impl::onWrite, shared_from_this()));
        }
    }

    void onWrite(beast::error_code ec, std::size_t bytes_transferred) {
        boost::ignore_unused(bytes_transferred);
