        }
    }

    void onRead(error_code ec, std::size_t) {
        if (ec) return onError(ec, "read");
        std::string msg(buffer_.data().data(), buffer_.size());
        buffer_.consume(buffer_.size());
        // Process message
        read();
    }

    void send(std::string data, std::string) {
        write_queue_.push_back(std::move(data));
        if (write_queue_.size() == 1) {
            if (device_settings_.use_ssl) {
                ws_->async_write(net::buffer(write_queue_.front()),
                    [self = shared_from_this()](error_code ec, std::size_t) {
                        self->onWrite(ec);
                    });
            } else {
                ws_tcp_->async_write(net::buffer(write_queue_.front()),
                    [self = shared_from_this()](error_code ec, std::size_t) {
                        self->onWrite(ec);
                    });
            }
        }
    }

    void onWrite(error_code ec) {
        if (ec) return onError(ec, "write");
        write_queue_.pop_front();
        if (!write_queue_.empty()) send("", "");
    }

    void onError(error_code& ec, std::string context) {
        log_->error("{}: {}", context, ec.message());
        close();
    }

    void enqueueWork(Work& work) {
        ioc_.post([&ioc_]() { /* Work processing */ });
    }

    void close() {
        if (device_settings_.use_ssl) {
            if (ws_ && ws_->is_open()) {
                ws_->async_close(close_code::normal, [](error_code) {});
            }
        } else {
            if (ws_tcp_ && ws_tcp_->is_open()) {
                ws_tcp_->async_close(close_code::normal, [](error_code) {});
            }
        }
        ws_.reset();
        ws_tcp_.reset();
    }

private:
    net::io_context& ioc_;
    ssl::context& ctx_;
    Device& device_settings_;
    std::shared_ptr<Logging> log_;
    tcp::resolver resolver_;
    std::optional<websocket::stream<ssl_stream<tcp_stream>>> ws_;
    std::optional<websocket::stream<tcp_stream>> ws_tcp_;
    flat_buffer buffer_;
    std::string host_;
    int socket_status_;
    std::deque<std::string> write_queue_;
};
