#include <vector>
#include <sys/socket.h>
#include <netinet/in.h>
#include <arpa/inet.h>
#include <unistd.h>
#include <system_error>
#include <chrono>
#include <thread>

class LinHttpHandler {
private:
    class SocketCloser {
    public:
        explicit SocketCloser(int sockFd) : fd(sockFd) {}
        ~SocketCloser() { if (fd >= 0) close(fd); }
        int get() const { return fd; }
    private:
        int fd;
    };

public:
    std::string send(const std::string& msg, const std::string& adr, int port) {
        int sockfd = socket(AF_INET, SOCK_STREAM, 0);
        if (sockfd < 0)
            throw std::system_error(errno, std::generic_category(), "socket");

        SocketCloser closer(sockfd);

        sockaddr_in serverAddr{};
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &serverAddr.sin_addr);

        if (connect(sockfd, reinterpret_cast<sockaddr*>(&serverAddr), sizeof(serverAddr)) < 0)
            throw std::system_error(errno, std::generic_category(), "connect");

        if (sendto(sockfd, msg.data(), msg.size(), 0, reinterpret_cast<sockaddr*>(&serverAddr), sizeof(serverAddr)) < 0)
            throw std::system_error(errno, std::generic_category(), "sendto");

        char buffer[4096];
        ssize_t bytesRead = recv(sockfd, buffer, sizeof(buffer) - 1, 0);
        if (bytesRead < 0)
            throw std::system_error(errno, std::generic_category(), "recv");

        return std::string(buffer, bytesRead);
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sockfd = socket(AF_INET, SOCK_DGRAM, 0);
        if (sockfd < 0)
            throw std::system_error(errno, std::generic_category(), "socket");

        SocketCloser closer(sockfd);

        int enable = 1;
        if (setsockopt(sockfd, SOL_SOCKET, SO_REUSEADDR, &enable, sizeof(enable)) < 0)
            throw std::system_error(errno, std::generic_category(), "setsockopt(SO_REUSEADDR)");

        sockaddr_in localAddr{};
        localAddr.sin_family = AF_INET;
        localAddr.sin_port = htons(port);
        localAddr.sin_addr.s_addr = htonl(INADDR_ANY);

        if (bind(sockfd, reinterpret_cast<sockaddr*>(&localAddr), sizeof(localAddr)) < 0)
            throw std::system_error(errno, std::generic_category(), "bind");

        ip_mreq mreq{};
        inet_pton(AF_INET, adr.c_str(), &mreq.imr_multiaddr);
        mreq.imr_interface.s_addr = htonl(INADDR_ANY);

        if (setsockopt(sockfd, IPPROTO_IP, IP_ADD_MEMBERSHIP, &mreq, sizeof(mreq)) < 0)
            throw std::system_error(errno, std::generic_category(), "setsockopt(IP_ADD_MEMBERSHIP)");

        sockaddr_in multicastAddr{};
        multicastAddr.sin_family = AF_INET;
        multicastAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &multicastAddr.sin_addr);

        if (sendto(sockfd, msg.data(), msg.size(), 0, reinterpret_cast<sockaddr*>(&multicastAddr), sizeof(multicastAddr)) < 0)
            throw std::system_error(errno, std::generic_category(), "sendto");

        std::vector<std::string> responses;
        auto start = std::chrono::steady_clock::now();
        do {
            char buffer[4096];
            sockaddr_in senderAddr{};
            socklen_t senderAddrLen = sizeof(senderAddr);
            ssize_t bytesRead = recvfrom(sockfd, buffer, sizeof(buffer) - 1, MSG_DONTWAIT,
                                         reinterpret_cast<sockaddr*>(&senderAddr), &senderAddrLen);

            if (bytesRead > 0) {
                buffer[bytesRead] = '\0';
                responses.emplace_back(buffer);
            } else if (bytesRead < 0 && errno != EAGAIN && errno != EWOULDBLOCK) {
                throw std::system_error(errno, std::generic_category(), "recvfrom");
            }

            std::this_thread::sleep_for(std::chrono::milliseconds(50));
        } while ((std::chrono::steady_clock::now() - start) < timeout);

        return responses;
    }
};

