class TCPClient {
public:
    using SocketHolder = int;
    TCPClient(const std::string& address, uint16_t port)
        : address(address), port(port), main_socket(INVALID_SOCKET), connected(false) {}

    ~TCPClient() {
        Disconnect();
    }

    bool Connect() {
        addrinfo hints{}, *servinfo = nullptr, *p = nullptr;
        memset(&hints, 0, sizeof(hints));
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_STREAM;

        if (getaddrinfo(address.c_str(), std::to_string(port).c_str(), &hints, &servinfo) != 0) {
            return false;
        }

        for (p = servinfo; p != nullptr; p = p->ai_next) {
            main_socket = socket(p->ai_family, p->ai_socktype, p->ai_protocol);
            if (main_socket == INVALID_SOCKET) continue;

            SetNonBlock(main_socket, true);

            if (ConnectWithTimeout(main_socket, p->ai_addr, p->ai_addrlen, 5) == true) {
                SetNonBlock(main_socket, false);
                connected = true;
                freeaddrinfo(servinfo);
                return true;
            }

            closesocket(main_socket);
            main_socket = INVALID_SOCKET;
        }

        freeaddrinfo(servinfo);
        return false;
    }

    bool Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected || !buffer || size == 0) return false;

        char* buf = static_cast<char*>(buffer);
        size_t total_read = 0;

        auto start_time = std::chrono::steady_clock::now();
        while (total_read < size) {
            auto now = std::chrono::steady_clock::now();
            if (now - start_time > timeout) {
                errno = ETIMEDOUT;
                return false;
            }

            ssize_t bytes_read = recv(main_socket, buf + total_read, size - total_read, 0);
            if (bytes_read > 0) {
                total_read += bytes_read;
            } else if (bytes_read == 0) {
                connected = false;
                return false;
            } else {
                if (errno != EAGAIN && errno != EWOULDBLOCK) {
                    connected = false;
                    return false;
                }
            }
        }

        return total_read == size;
    }

    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected || !buffer || size == 0) return false;

        const char* buf = static_cast<const char*>(buffer);
        size_t total_written = 0;

        auto start_time = std::chrono::steady_clock::now();
        while (total_written < size) {
            auto now = std::chrono::steady_clock::now();
            if (now - start_time > timeout) {
                errno = ETIMEDOUT;
                return false;
            }

            ssize_t bytes_sent = send(main_socket, buf + total_written, size - total_written, 0);
            if (bytes_sent > 0) {
                total_written += bytes_sent;
            } else if (bytes_sent == 0) {
                connected = false;
                return false;
            } else {
                if (errno != EAGAIN && errno != EWOULDBLOCK) {
                    connected = false;
                    return false;
                }
            }
        }

        return total_written == size;
    }

    void Disconnect() {
        if (main_socket != INVALID_SOCKET) {
            closesocket(main_socket);
            main_socket = INVALID_SOCKET;
        }
        connected = false;
    }

    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
        if (sockFD == INVALID_SOCKET) return false;

#ifdef _WIN32
        u_long mode = nonBlocking ? 1 : 0;
        return ioctlsocket(sockFD, FIONBIO, &mode) == 0;
#else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) return false;
        flags = nonBlocking ? (flags | O_NONBLOCK) : (flags & ~O_NONBLOCK);
        return fcntl(sockFD, F_SETFL, flags) != -1;
#endif
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        if (connect(sockFD, reinterpret_cast<SOCKADDR*>(server_addr), server_addr_len) == 0) {
            return true;
        }

        if (errno != EINPROGRESS) {
            return false;
        }

        fd_set write_fds, except_fds;
        FD_ZERO(&write_fds);
        FD_ZERO(&except_fds);
        FD_SET(sockFD, &write_fds);
        FD_SET(sockFD, &except_fds);

        timeval tv{};
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        int select_result = select(sockFD + 1, nullptr, &write_fds, &except_fds, &tv);
        if (select_result <= 0) {
            return false;
        }

        if (FD_ISSET(sockFD, &except_fds)) {
            return false;
        }

        socklen_t so_error_len = sizeof(errno);
        getsockopt(sockFD, SOL_SOCKET, SO_ERROR, reinterpret_cast<char*>(&errno), &so_error_len);

        return errno == 0;
    }

private:
    std::string address;
    uint16_t port;
    SocketHolder main_socket;
    bool connected;
};

