#include <sys/socket.h>
#include <arpa/inet.h>
#include <fcntl.h>
#include <sys/types.h>

typedef struct {
    char* ip;
    unsigned short port;
} stEndPoint;

void* readwrite_routine(void* arg) {
    stEndPoint* endpoint = (stEndPoint*)arg;
    const char* message = "Hello from client";
    const int reconnect_delay_ms = 5000;

    while (1) {
        int sock_fd = -1;
        struct sockaddr_in server_addr;

        // Create socket
        if ((sock_fd = socket(AF_INET, SOCK_STREAM, 0)) == -1) {
            perror("Socket creation failed");
            usleep(reconnect_delay_ms * 1000);
            continue;
        }

        // Set non-blocking
        int flags = fcntl(sock_fd, F_GETFL, 0);
        fcntl(sock_fd, F_SETFL, flags | O_NONBLOCK);

        // Initialize server address struct
        memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(endpoint->port);

        // Handle special IP cases
        if (endpoint->ip == NULL || strcmp(endpoint->ip, "INADDR_ANY") == 0) {
            server_addr.sin_addr.s_addr = INADDR_ANY;
        } else {
            if (inet_pton(AF_INET, endpoint->ip, &server_addr.sin_addr) <= 0) {
                perror("Invalid address");
                close(sock_fd);
                usleep(reconnect_delay_ms * 1000);
                continue;
            }
        }

        // Connect to server
        int conn_result = connect(sock_fd, (struct sockaddr*)&server_addr, sizeof(server_addr));

        if (conn_result == -1) {
            if (errno != EINPROGRESS) {
                perror("Connection failed");
                close(sock_fd);
                usleep(reconnect_delay_ms * 1000);
                continue;
            }
        }

        // Set back to blocking mode
        fcntl(sock_fd, F_SETFL, flags);

        // Communication loop
        while (1) {
            // Send data
            ssize_t sent = send(sock_fd, message, strlen(message), 0);
            if (sent <= 0) {
                perror("Send failed");
                break;
            }

            // Receive response
            char buffer[1024];
            ssize_t received;
            while ((received = recv(sock_fd, buffer, sizeof(buffer)-1, 0)) > 0) {
                buffer[received] = '\0';
                printf("Received: %s\n", buffer);
            }

            if (received == 0) {
                printf("Server disconnected\n");
                break;
            } else if (received < 0) {
                if (errno != EAGAIN && errno != EWOULDBLOCK) {
                    perror("Receive failed");
                    break;
                }
            }

            // Small delay before next send
            usleep(100000); // 100ms
        }

        // Close socket and prepare for reconnection
        close(sock_fd);
    }

    return NULL;
}

