#include <functional>
#include <memory>
#include <sstream>
#include <vector>
#include <algorithm>

class Stats {
public:
    explicit Stats(const std::string& stats_socket_path)
        : stats_socket_path_(stats_socket_path),
          sockfd_(-1),
          serv_addr_(),
          stats_thread_(),
          statsThreadRunning_(false),
          stats_mutex_(),
          thread_mutex_(),
          thread_exited_(),
          thread_count_(0),
          stats_() {
        memset(&serv_addr_, 0, sizeof(serv_addr_));
        serv_addr_.sun_family = AF_UNIX;
        strncpy(serv_addr_.sun_path, stats_socket_path_.c_str(), sizeof(serv_addr_.sun_path) - 1);
    }

    ~Stats() {
        stop();
        if (sockfd_ != -1) {
            close(sockfd_);
            unlink(serv_addr_.sun_path);
        }
    }

    static Stats& get(const std::string& stats_socket_path) {
        static Stats instance(stats_socket_path);
        return instance;
    }

    static std::unique_ptr<Stats> get_for_unittest(const std::string& stats_socket_path) {
        return std::make_unique<Stats>(stats_socket_path);
    }

    static bool init(const std::string& stats_socket_path) {
        if (!isInitInternal()) {
            try {
                Stats& instance = get(stats_socket_path);
                if (instance.startSocket()) {
                    setIsInitInternal(true);
                    return true;
                }
            } catch (...) {
                setIsInitInternal(false);
            }
        }
        return isInitInternal();
    }

    static bool isInit() {
        return isInitInternal();
    }

    std::unordered_map<std::string, int> getAll() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_;
    }

    int increment(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] += val;
        return stats_[key];
    }

    int set(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] = val;
        return val;
    }

    int reset() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_.clear();
        return 0;
    }

private:
    std::string stats_socket_path_;
    int sockfd_;
    struct sockaddr_un serv_addr_;
    std::thread stats_thread_;
    std::atomic<bool> statsThreadRunning_;
    std::mutex stats_mutex_;
    std::mutex thread_mutex_;
    std::condition_variable thread_exited_;
    int thread_count_;
    std::unordered_map<std::string, int> stats_;

    static bool& isInitInternal() {
        static bool inited = false;
        return inited;
    }

    static void setIsInitInternal(bool value) {
        isInitInternal() = value;
    }

    bool startSocket() {
        if (sockfd_ != -1) {
            return true;
        }

        sockfd_ = socket(AF_UNIX, SOCK_STREAM, 0);
        if (sockfd_ == -1) {
            return false;
        }

        unlink(serv_addr_.sun_path);

        if (bind(sockfd_, (struct sockaddr*)&serv_addr_, sizeof(serv_addr_)) == -1) {
            close(sockfd_);
            sockfd_ = -1;
            return false;
        }

        if (listen(sockfd_, 5) == -1) {
            close(sockfd_);
            sockfd_ = -1;
            unlink(serv_addr_.sun_path);
            return false;
        }

        statsThreadRunning_ = true;
        stats_thread_ = std::thread(&Stats::runSocket, this);
        return true;
    }

    void runSocket() {
        while (statsThreadRunning_) {
            int client_fd = accept(sockfd_, nullptr, nullptr);
            if (client_fd >= 0 && statsThreadRunning_) {
                std::lock_guard<std::mutex> lock(thread_mutex_);
                ++thread_count_;
                std::thread([this, client_fd]() {
                    processMsg(client_fd);
                    close(client_fd);
                    std::lock_guard<std::mutex> lock(thread_mutex_);
                    --thread_count_;
                    thread_exited_.notify_one();
                }).detach();
            }
        }

        std::unique_lock<std::mutex> lock(thread_mutex_);
        thread_exited_.wait(lock, [this] { return thread_count_ == 0; });
    }

    void processMsg(int sockfd) {
        char buffer[4096];
        ssize_t bytes_read = read(sockfd, buffer, sizeof(buffer) - 1);
        if (bytes_read <= 0) {
            return;
        }

        buffer[bytes_read] = '\0';
        std::string msg(buffer);
        std::istringstream iss(msg);
        std::string command;
        if (!(iss >> command)) {
            return;
        }

        std::ostringstream response;
        if (command == "GETALL") {
            auto all_stats = getAll();
            for (const auto& pair : all_stats) {
                response << pair.first << ":" << pair.second << "\n";
            }
        } else if (command == "RESET") {
            reset();
            response << "OK\n";
        } else if (command == "GET") {
            std::string key;
            if (iss >> key) {
                std::lock_guard<std::mutex> lock(stats_mutex_);
                auto it = stats_.find(key);
                if (it != stats_.end()) {
                    response << it->second << "\n";
                } else {
                    response << "0\n";
                }
            }
        } else if (command == "INCREMENT") {
            std::string key;
            int val;
            if (iss >> key >> val) {
                int new_val = increment(key, val);
                response << new_val << "\n";
            }
        } else if (command == "SET") {
            std::string key;
            int val;
            if (iss >> key >> val) {
                int new_val = set(key, val);
                response << new_val << "\n";
            }
        } else {
            response << "UNKNOWN_COMMAND\n";
        }

        std::string resp_str = response.str();
        write(sockfd, resp_str.c_str(), resp_str.size());
    }

    void stop() {
        if (statsThreadRunning_.exchange(false)) {
            shutdown(sockfd_, SHUT_RDWR);
            if (stats_thread_.joinable()) {
                stats_thread_.join();
            }
        }
    }
};

