class DFNetWork;

class UDPRecv {
public:
    UDPRecv(DFNetWork* poDFNetWork);
    ~UDPRecv();
    void Stop();
    int Init(int iPort);
    void run();

private:
    DFNetWork* m_poDFNetWork;
    int m_iSockFD;
    bool m_bIsEnd;
    bool m_bIsStarted;
    std::thread m_oThread;
    std::mutex m_oMutex;
};

UDPRecv::UDPRecv(DFNetWork* poDFNetWork)
    : m_poDFNetWork(poDFNetWork), m_iSockFD(-1), m_bIsEnd(false), m_bIsStarted(false) {}

UDPRecv::~UDPRecv() {
    if (m_iSockFD != -1) {
        close(m_iSockFD);
        m_iSockFD = -1;
    }
}

void UDPRecv::Stop() {
    std::unique_lock<std::mutex> lock(m_oMutex);
    m_bIsEnd = true;
    if (m_oThread.joinable()) {
        lock.unlock();
        m_oThread.join();
    }
}

int UDPRecv::Init(int iPort) {
    m_iSockFD = socket(AF_INET, SOCK_DGRAM, 0);
    if (m_iSockFD == -1) {
        return -1;
    }

    sockaddr_in addr;
    std::memset(&addr, 0, sizeof(addr));
    addr.sin_family = AF_INET;
    addr.sin_addr.s_addr = INADDR_ANY;
    addr.sin_port = htons(iPort);

    if (bind(m_iSockFD, reinterpret_cast<struct sockaddr*>(&addr), sizeof(addr)) == -1) {
        close(m_iSockFD);
        m_iSockFD = -1;
        return -1;
    }

    // Set non-blocking
    int flags = fcntl(m_iSockFD, F_GETFL, 0);
    fcntl(m_iSockFD, F_SETFL, flags | O_NONBLOCK);

    m_bIsStarted = true;
    m_oThread = std::thread([this]() { this->run(); });
    return 0;
}

void UDPRecv::run() {
    const int MAX_BUFFER_SIZE = 65536;
    char buffer[MAX_BUFFER_SIZE];
    sockaddr_in senderAddr;
    socklen_t senderAddrLen = sizeof(senderAddr);

    struct pollfd pfd;
    pfd.fd = m_iSockFD;
    pfd.events = POLLIN;

    while (!m_bIsEnd) {
        int ret = poll(&pfd, 1, 100); // 100ms timeout
        if (ret > 0 && (pfd.revents & POLLIN)) {
            ssize_t recvLen = recvfrom(m_iSockFD, buffer, MAX_BUFFER_SIZE, 0,
                                       reinterpret_cast<sockaddr*>(&senderAddr), &senderAddrLen);
            if (recvLen > 0) {
                // Forward the received packet to network handler
                m_poDFNetWork->HandleMessage(buffer, recvLen, &senderAddr);
            }
        }
    }

    m_bIsStarted = false;
}

