class DownloadTask {
public:
    static void initialize() { curl_global_init(CURL_GLOBAL_DEFAULT); }
    static void uninitialize() { curl_global_cleanup(); }

    DownloadTask(const std::string& url, const std::string& output, const std::string& defaultFile, time_t mtime, const std::string& name = "")
        : m_url(url), m_urlBackup(""), m_output(output), m_outputTmp(output + ".tmp"), m_default(defaultFile),
          m_mtime(mtime), m_retries(0), m_name(name), m_userAgent("DownloadTask/1.0"), m_error("") {}

    bool run() {
        if (std::ifstream(m_output).good()) return true;

        for (unsigned int attempt = 0; attempt <= 3; ++attempt) {
            m_retries = attempt;
            if (downloadFile(m_url) || (m_urlBackup != "" && downloadFile(m_urlBackup))) {
                if (rename(m_outputTmp.c_str(), m_output.c_str()) == 0) {
                    struct utimbuf newTime;
                    newTime.actime = m_mtime;
                    newTime.modtime = m_mtime;
                    utime(m_output.c_str(), &newTime);
                    return true;
                }
            }
            if (attempt < 2) continue;
            break;
        }

        if (!m_default.empty()) {
            rename(m_default.c_str(), m_output.c_str());
        }
        return false;
    }

    unsigned int getRetries() const { return m_retries; }

    size_t writeData(void* contents, size_t size, size_t nmemb) {
        size_t totalSize = size * nmemb;
        std::ofstream* file = new std::ofstream(m_outputTmp, std::ios::binary | std::ios::app);
        file->write(static_cast<char*>(contents), totalSize);
        file->close();
        delete file;
        return totalSize;
    }

private:
    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) return false;

        FILE* fp = fopen(m_outputTmp.c_str(), "wb");
        if (!fp) {
            m_error = "Failed to open file: " + m_outputTmp;
            return false;
        }

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &DownloadTask::curlWriteCallback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, this);
        curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);

        CURLcode res = curl_easy_perform(curl);
        fclose(fp);
        curl_easy_cleanup(curl);

        if (res != CURLE_OK) {
            m_error = "curl_easy_perform() failed: " + std::string(curl_easy_strerror(res));
            remove(m_outputTmp.c_str());
            return false;
        }
        return true;
    }

    static size_t curlWriteCallback(void* contents, size_t size, size_t nmemb, void* userp) {
        return static_cast<DownloadTask*>(userp)->writeData(contents, size, nmemb);
    }

    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;
};

