class HttpWebResponse {
public:
    std::string content;
    int statusCode;

    HttpWebResponse(std::string content, int statusCode) : content(content), statusCode(statusCode) {}
};

class HttpWebRequest {
private:
    std::string uri_;
    std::string method_;
    std::map<std::string, std::string> header_;
    std::iostream is_;

    static size_t callback(void *buffer, size_t size, size_t nmemb, void *data) {
        ((std::stringstream*)data)->write((char*)buffer, (int)(size * nmemb));
        return size * nmemb;
    }

    HttpWebResponse* GetFileResponse(const char *uri) {
        // Simplified file handling for example purposes
        FILE *file = fopen(uri + 7, "rb"); // skip file://
        if (!file) {
            return new HttpWebResponse("", 404);
        }

        fseek(file, 0, SEEK_END);
        long length = ftell(file);
        fseek(file, 0, SEEK_SET);

        char *content = new char[length];
        fread(content, 1, length, file);
        fclose(file);

        std::string result(content, length);
        delete[] content;

        return new HttpWebResponse(result, 200);
    }

    HttpWebResponse* GetNetResponse(const char *uri) {
        CURL *curl;
        CURLcode res;
        std::stringstream out;
        struct curl_slist *headers = NULL;

        curl = curl_easy_init();
        if (!curl) return new HttpWebResponse("", 500);

        for (auto const &header : header_) {
            std::string headerStr = header.first + ": " + header.second;
            headers = curl_slist_append(headers, headerStr.c_str());
        }

        curl_easy_setopt(curl, CURLOPT_URL, uri);
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, callback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, &out);
        curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headers);
        curl_easy_setopt(curl, CURLOPT_CUSTOMREQUEST, method_.c_str());

        res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            curl_easy_cleanup(curl);
            curl_slist_free_all(headers);
            return new HttpWebResponse("", 500);
        }

        long http_code = 0;
        curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &http_code);
        curl_easy_cleanup(curl);
        curl_slist_free_all(headers);

        return new HttpWebResponse(out.str(), (int)http_code);
    }

public:
    HttpWebRequest(std::string &uri) : uri_(uri), method_("GET"), is_() {}

    HttpWebResponse* GetResponse() {
        if (uri_.substr(0, 7) == "file://") {
            return GetFileResponse(uri_.c_str());
        } else {
            return GetNetResponse(uri_.c_str());
        }
    }

    void setMethod(const std::string &method) {
        method_ = method;
    }

    void addHeader(const std::string &key, const std::string &value) {
        header_[key] = value;
    }
};

