void pipe_communication() {
    int fd[2];
    pid_t pid;
    const char* message = "Hello from parent process";

    if (pipe(fd) == -1) {
        std::cerr << "pipe create error" << std::endl;
        return;
    }

    pid = fork();

    if (pid < 0) {
        std::cerr << "fork failed" << std::endl;
        return;
    }

    if (pid > 0) { // Parent process
        close(fd[0]); // Close reading end of the pipe
        std::cout << "Write data " << message << std::endl;
        write(fd[1], message, strlen(message)+1);
        close(fd[1]); // Close writing end after writing
        std::cout << "Parent write over" << std::endl;
        wait(nullptr); // Wait for child to finish
    } else { // Child process
        close(fd[1]); // Close writing end of the pipe
        char buffer[256];
        read(fd[0], buffer, sizeof(buffer));
        std::cout << "Read data from the pipe is " << buffer << std::endl;
        close(fd[0]); // Close reading end after reading
    }
}

