class Task {
public:
    std::string m_url;
    std::string m_output;

    Task(const std::string& url, const std::string& output) : m_url(url), m_output(output) {}

    size_t writeData(void* buffer, size_t size, size_t nmemb) {
        std::ofstream* file = reinterpret_cast<std::ofstream*>(m_output.c_str());
        if (file && file->is_open()) {
            file->write(static_cast<char*>(buffer), size * nmemb);
            return size * nmemb;
        }
        return 0;
    }

    void run() {
        std::ofstream outFile(m_output, std::ios::binary);
        if (!outFile.is_open()) {
            std::cerr << "Failed to open output file: " << m_output << std::endl;
            return;
        }

        CURL* curl = curl_easy_init();
        if (!curl) {
            std::cerr << "Failed to initialize CURL" << std::endl;
            outFile.close();
            return;
        }

        curl_easy_setopt(curl, CURLOPT_URL, m_url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &Task::writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, &outFile);
        curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);

        CURLcode res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            std::cerr << "CURL error: " << curl_easy_strerror(res) << std::endl;
        }

        curl_easy_cleanup(curl);
        outFile.close();
    }
};

class DownloadPool {
private:
    std::vector<std::thread> m_threads;
    std::queue<Task> m_queue;
    std::unordered_set<std::string> m_urls;
    std::mutex m_mtx;
    std::condition_variable m_cv;
    bool m_noMoreTask = false;

public:
    DownloadPool(int numThreads) {
        curl_global_init(CURL_GLOBAL_DEFAULT);
        for (int i = 0; i < numThreads; ++i) {
            m_threads.emplace_back(&DownloadPool::run, this);
        }
    }

    ~DownloadPool() {
        finishAndWaitForExit();
        curl_global_cleanup();
    }

    void addTask(const std::string& url, const std::string& output) {
        std::unique_lock<std::mutex> lock(m_mtx);
        if (m_urls.find(url) == m_urls.end()) {
            m_queue.emplace(url, output);
            m_urls.insert(url);
            lock.unlock();
            m_cv.notify_one();
        }
    }

    void setNoMoreTask() {
        std::lock_guard<std::mutex> lock(m_mtx);
        m_noMoreTask = true;
        m_cv.notify_all();
    }

    void finishAndWaitForExit() {
        setNoMoreTask();
        for (auto& thread : m_threads) {
            if (thread.joinable()) {
                thread.join();
            }
        }
    }

    void run() {
        while (true) {
            std::unique_lock<std::mutex> lock(m_mtx);
            m_cv.wait(lock, [this] { return !m_queue.empty() || m_noMoreTask; });

            if (m_queue.empty() && m_noMoreTask) {
                return;
            }

            if (!m_queue.empty()) {
                Task task = m_queue.front();
                m_queue.pop();
                lock.unlock();

                task.run();
            }
        }
    }
};

