class chat_message {
public:
    enum { header_length = 4 };
    enum { max_body_length = 512 };

    chat_message() : body_length_(0) {}

    const char* data() const { return data_; }
    char* data() { return data_; }
    std::size_t length() const { return header_length + body_length_; }

    const char* body() const { return data_ + header_length; }
    char* body() { return data_ + header_length; }
    std::size_t body_length() const { return body_length_; }

    void body_length(std::size_t new_length) {
        body_length_ = new_length;
        if (body_length_ > max_body_length)
            body_length_ = max_body_length;
    }

    bool decode_header() {
        using namespace std; // For strncat and atoi
        char header[header_length + 1] = "";
        strncat(header, data_, header_length);
        body_length_ = atoi(header);
        if (body_length_ > max_body_length)
            return false;
        return true;
    }

    void encode_header() {
        using namespace std; // For sprintf and memcpy
        char header[header_length + 1];
        sprintf(header, "%4d", static_cast<int>(body_length_));
        memcpy(data_, header, header_length);
    }

private:
    char data_[header_length + max_body_length];
    std::size_t body_length_;
};

class posix_chat_client : public std::enable_shared_from_this<posix_chat_client> {
public:
    posix_chat_client(boost::asio::io_context& io_context, const tcp::resolver::results_type& endpoints)
