#include <boost/asio.hpp>
#include <boost/beast/websocket.hpp>
#include <boost/beast/websocket/ssl.hpp>
#include <memory>
#include <string>
#include <queue>
#include <variant>

namespace net = boost::asio;
namespace ssl = boost::asio::ssl;
namespace beast = boost::beast;
namespace websocket = beast::websocket;

using tcp = boost::asio::ip::tcp;

class Logging {
public:
    virtual void log(const std::string&) = 0;
};

struct Device {
    std::string host;
    bool use_ssl;
};

class WebOsClient {
private:
    class Impl {
    public:
        using Work = std::function<void()>;

        Impl(net::io_context& ioc, ssl::context& ctx, Device& device, Logging& log)
            : ioc_(ioc), ctx_(ctx), device_settings_(device), log_(std::make_shared<Logging>(log)),
              resolver_(ioc_), buffer_(8192) {}

        void run() {
            host_ = device_settings_.host;
            resolver_.async_resolve(tcp::resolver::query(host_, "80"),
                [self = shared_from_this()](beast::error_code ec, tcp::resolver::results_type results) {
                    self->onResolve(ec, results);
                });
        }

        void onResolve(beast::error_code ec, tcp::resolver::results_type results) {
            if (ec) {
                onError(ec, "resolve");
                return;
            }

            if (device_settings_.use_ssl) {
                ws_ = websocket::stream<beast::ssl_stream<beast::tcp_stream>>(ioc_, ctx_);
                auto& s = ws_->next_layer().next_layer();
                s.async_connect(results->endpoint(),
                    [self = shared_from_this(), ep = results->endpoint()](beast::error_code ec) {
                        self->onConnect(ec, ep);
                    });
            } else {
                ws_tcp_ = websocket::stream<beast::tcp_stream>(ioc_);
                auto& s = ws_tcp_->next_layer();
                s.async_connect(results->endpoint(),
                    [self = shared_from_this(), ep = results->endpoint()](beast::error_code ec) {
                        self->onConnect(ec, ep);
                    });
            }
        }

        void onConnect(beast::error_code ec, tcp::resolver::results_type::endpoint_type) {
            if (ec) {
                onError(ec, "connect");
                return;
            }

            if (device_settings_.use_ssl) {
                ws_->next_layer().async_handshake(ssl::stream_base::client,
                    [self = shared_from_this()](beast::error_code ec) {
                        self->onSSLhandshake(ec);
                    });
            } else {
                ws_tcp_->async_handshake(host_, "/",
                    [self = shared_from_this()](beast::error_code ec) {
                        self->onWinsockHandshake(ec);
                    });
            }
        }

        void onSSLhandshake(beast::error_code ec) {
            if (ec) {
                onError(ec, "ssl handshake");
                return;
            }

            ws_->async_handshake(host_, "/",
                [self = shared_from_this()](beast::error_code ec) {
                    self->onWinsockHandshake(ec);
                });
        }

        void onWinsockHandshake(beast::error_code ec) {
            if (ec) {
                onError(ec, "websocket handshake");
                return;
            }

            socket_status_ = 1; // connected
            read();
        }

        void send(std::string text_type, std::string data) {
            if (socket_status_ != 1) return;

            auto work = std::make_shared<std::string>(std::move(data));
            if (device_settings_.use_ssl) {
                ws_->async_write(net::buffer(*work),
                    [self = shared_from_this()](beast::error_code ec, std::size_t bytes_transferred) {
                        self->onWrite(ec, bytes_transferred);
                    });
            } else {
                ws_tcp_->async_write(net::buffer(*work),
                    [self = shared_from_this()](beast::error_code ec, std::size_t bytes_transferred) {
                        self->onWrite(ec, bytes_transferred);
                    });
            }
        }

        void onWrite(beast::error_code ec, std::size_t) {
            if (ec) {
                onError(ec, "write");
                return;
            }
        }

        void read() {
            if (socket_status_ != 1) return;

            if (device_settings_.use_ssl) {
                ws_->async_read(buffer_,
                    [self = shared_from_this()](beast::error_code ec, std::size_t bytes_transferred) {
                        self->onRead(ec, bytes_transferred);
                    });
            } else {
                ws_tcp_->async_read(buffer_,
                    [self = shared_from_this()](beast::error_code ec, std::size_t bytes_transferred) {
                        self->onRead(ec, bytes_transferred);
                    });
            }
        }

        void onRead(beast::error_code ec, std::size_t) {
            if (ec) {
                onError(ec, "read");
                return;
            }

            // Process message here

            read();
        }

        void onError(beast::error_code& ec, std::string stage) {
            if (ec == websocket::error::closed || ec == beast::errc::operation_aborted) {
                socket_status_ = 0; // disconnected
                return;
            }

            // Handle error and potentially reconnect
            socket_status_ = 0; // disconnected
        }

        void enqueueWork(Work& work) {
            ioc_.post(work);
        }

        void close() {
            socket_status_ = 0; // disconnecting

            if (device_settings_.use_ssl) {
                ws_->async_close(websocket::close_code::normal,
                    [self = shared_from_this()](beast::error_code ec) {});
            } else {
                ws_tcp_->async_close(websocket::close_code::normal,
                    [self = shared_from_this()](beast::error_code ec) {});
            }
        }

    private:
        net::io_context& ioc_;
        ssl::context& ctx_;
        Device& device_settings_;
        std::shared_ptr<Logging> log_;
        tcp::resolver resolver_;
        std::optional<websocket::stream<beast::ssl_stream<beast::tcp_stream>>> ws_;
        std::optional<websocket::stream<beast::tcp_stream>> ws_tcp_;
        beast::flat_buffer buffer_;
        std::string host_;
        int socket_status_ = 0; // 0: disconnected, 1: connecting, 2: connected
    };
};

