      OLOG << "Stats server error: reading from socket: "
           << ::strerror_r(errno, err_buf.data(), err_buf.size());
      return;
    } else if (res == 0) {
      break;
    }
    if (byte_buf == '\n' || byte_buf == '\0') {
      break;
    }
    if (num_read == 0) {
      mode = byte_buf;
    }
  }

  Json::Value root;
  root["error"] = 0;
  Json::Value body(Json::objectValue);
  switch (mode) {
    case 'g':
      for (auto const& pair : getAll()) {
        body[pair.first] = pair.second;
      }
