                    do_read_header();
                }
            });
    }

    void do_write() {
        if (write_msgs_.empty()) return;
        auto msg = write_msgs_.front();
        boost::asio::async_write(socket_, boost::asio::buffer(msg.header, sizeof(msg.header)),
            [this, msg](boost::system::error_code ec, std::size_t bytes_transferred) {
                if (!ec) {
                    boost::asio::async_write(socket_, boost::asio::buffer(msg.body, sizeof(msg.body)),
                        [this](boost::system::error_code ec, std::size_t bytes_transferred) {
                            if (!ec) {
                                write_msgs_.pop();
                                do_write();
                            }
                        });
                }
            });
    }

    boost::asio::io_context& io_context_;
    boost::asio::ip::tcp::socket socket_;
    chat::chat_message read_msg_;
    chat::chat_message_queue write_msgs_;
};
