        }

        size_t total = msg.length();
        size_t sent = 0;
        do
        {
            ssize_t bytes = write(socketFD, msg.c_str() + sent, total - sent);
            if (bytes < 0)
            {
                int errCode = errno;
                throw(std::system_error(errCode, std::generic_category(), "LinHttpHandler: Failed to write message to socket"));
            }
            sent += bytes;
        } while (sent < total);

        std::string response;
        char buffer[128] = {};
