#include <sys/socket.h>
#include <netinet/in.h>
#include <arpa/inet.h>
#include <netdb.h>
#include <unistd.h>
#include <vector>
#include <string>
#include <system_error>
#include <chrono>
#include <cstring>
#include <fcntl.h>
#include <sys/select.h>

class LinHttpHandler {
public:
    class SocketCloser {
        int sockFd;
    public:
        SocketCloser(int fd) : sockFd(fd) {}
        ~SocketCloser() { close(sockFd); }
    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        int sock = socket(AF_INET, SOCK_STREAM, 0);
        if (sock < 0) throw std::system_error(errno, std::generic_category(), "Socket creation failed");
        SocketCloser closer(sock);

        addrinfo hints = {};
        hints.ai_family = AF_INET;
        hints.ai_socktype = SOCK_STREAM;
        addrinfo* res;
        int err = getaddrinfo(adr.c_str(), std::to_string(port).c_str(), &hints, &res);
        if (err) throw std::system_error(err, std::generic_category(), "Address resolution failed");

        if (connect(sock, res->ai_addr, res->ai_addrlen) < 0) {
            freeaddrinfo(res);
            throw std::system_error(errno, std::generic_category(), "Connect failed");
        }
        freeaddrinfo(res);

        ssize_t bytesSent = send(sock, msg.c_str(), msg.size(), 0);
        if (bytesSent < 0) {
            throw std::system_error(errno, std::generic_category(), "Send failed");
        }

        std::string response;
        char buffer[4096];
        while (true) {
            ssize_t bytesRecv = recv(sock, buffer, sizeof(buffer), 0);
            if (bytesRecv <= 0) break;
            response.append(buffer, bytesRecv);
        }

        return response;
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sock = socket(AF_INET, SOCK_DGRAM, 0);
        if (sock < 0) throw std::system_error(errno, std::generic_category(), "Socket creation failed");
        SocketCloser closer(sock);

        int opt = 1;
        if (setsockopt(sock, SOL_SOCKET, SO_REUSEADDR, &opt, sizeof(opt)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Setsockopt SO_REUSEADDR failed");
        }

        int ttl = 1;
        if (setsockopt(sock, IPPROTO_IP, IP_MULTICAST_TTL, &ttl, sizeof(ttl)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Setsockopt IP_MULTICAST_TTL failed");
        }

        sockaddr_in localAddr = {};
        localAddr.sin_family = AF_INET;
        localAddr.sin_addr.s_addr = INADDR_ANY;
        localAddr.sin_port = 0;

        if (bind(sock, (sockaddr*)&localAddr, sizeof(localAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Bind failed");
        }

        ip_mreq mreq = {};
        mreq.imr_multiaddr.s_addr = inet_addr(adr.c_str());
        mreq.imr_interface.s_addr = INADDR_ANY;

        if (setsockopt(sock, IPPROTO_IP, IP_ADD_MEMBERSHIP, &mreq, sizeof(mreq)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Setsockopt IP_ADD_MEMBERSHIP failed");
        }

        sockaddr_in destAddr = {};
        destAddr.sin_family = AF_INET;
        destAddr.sin_addr.s_addr = inet_addr(adr.c_str());
        destAddr.sin_port = htons(port);

        ssize_t bytesSent = sendto(sock, msg.c_str(), msg.size(), 0, (sockaddr*)&destAddr, sizeof(destAddr));
        if (bytesSent < 0) {
            throw std::system_error(errno, std::generic_category(), "Sendto failed");
        }

        std::vector<std::string> responses;
        fd_set read_fds;
        FD_ZERO(&read_fds);
        FD_SET(sock, &read_fds);

        auto timeout_ms = std::chrono::duration_cast<std::chrono::milliseconds>(timeout).count();
        timeval tv = { timeout_ms / 1000, (timeout_ms % 1000) * 1000 };

        while (true) {
            fd_set tmp_fds = read_fds;
            int activity = select(sock + 1, &tmp_fds, nullptr, nullptr, &tv);
            if (activity < 0) {
                throw std::system_error(errno, std::generic_category(), "Select failed");
            }
            if (activity == 0) break;

            if (FD_ISSET(sock, &tmp_fds)) {
                char buffer[4096];
                sockaddr_in fromAddr;
                socklen_t fromAddrLen = sizeof(fromAddr);
                ssize_t bytesRecv = recvfrom(sock, buffer, sizeof(buffer), 0, (sockaddr*)&fromAddr, &fromAddrLen);
                if (bytesRecv > 0) {
                    responses.push_back(std::string(buffer, bytesRecv));
                }
            }
        }

        return responses;
    }
};

