class ClientSocket {
public:
    ClientSocket(ThreadCtx& threadCtx, const std::string& dest, int port, const EncryptionParams& encryptionParams, int64_t ivChangeInterval)
        : threadCtx_(threadCtx), dest_(dest), port_(port), encryptionParams_(encryptionParams), ivChangeInterval_(ivChangeInterval) {}

    enum class ErrorCode {
        SUCCESS,
        ADDRESS_RESOLUTION_FAILURE,
        CONNECTION_FAILURE
    };

    ErrorCode connect() {
        struct addrinfo hints = {};
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_STREAM;
        hints.ai_flags = AI_ADDRCONFIG;

        struct addrinfo* res = nullptr;
        int status = getaddrinfo(dest_.c_str(), std::to_string(port_).c_str(), &hints, &res);
        if (status != 0) {
            return ErrorCode::ADDRESS_RESOLUTION_FAILURE;
        }

        for (struct addrinfo* ptr = res; ptr != nullptr; ptr = ptr->ai_next) {
            int sockfd = socket(ptr->ai_family, ptr->ai_socktype, ptr->ai_protocol);
            if (sockfd < 0) {
                continue;
            }

            int flags = fcntl(sockfd, F_GETFL, 0);
            fcntl(sockfd, F_SETFL, flags | O_NONBLOCK);

            socket_ = std::make_unique<WdtSocket>(sockfd);
            setSendBufferSize();

            int connectResult = socket_->connect(ptr->ai_addr, ptr->ai_addrlen);
            if (connectResult == 0) {
                getPeerIp(ptr->ai_addr);
                freeaddrinfo(res);
                return ErrorCode::SUCCESS;
            } else if (errno == EINPROGRESS) {
                fd_set writefds;
                FD_ZERO(&writefds);
                FD_SET(sockfd, &writefds);

                timeval timeout = { threadCtx_.timeoutSeconds, 0 };
                int selectResult = select(sockfd + 1, nullptr, &writefds, nullptr, &timeout);
                if (selectResult > 0) {
                    int error = 0;
                    socklen_t error_len = sizeof(error);
                    getsockopt(sockfd, SOL_SOCKET, SO_ERROR, &error, &error_len);
                    if (error == 0) {
                        getPeerIp(ptr->ai_addr);
                        freeaddrinfo(res);
                        return ErrorCode::SUCCESS;
                    }
                }
                socket_->~WdtSocket();
                close(sockfd);
            }
        }

        freeaddrinfo(res);
        return ErrorCode::CONNECTION_FAILURE;
    }

    const std::string& getPeerIp() const {
        return peerIp_;
    }

    void setSendBufferSize() {
        int bufferSize = threadCtx_.sendBufferSize;
        socket_->set_send_buffer_size(bufferSize);
    }

private:
    void closeConnection() {
        if (socket_) {
            socket_->~WdtSocket();
            socket_.reset();
        }
    }

    void getPeerIp(const sockaddr* addr) {
        char ipStr[INET6_ADDRSTRLEN];
        if (addr->sa_family == AF_INET) {
            inet_ntop(AF_INET, &((struct sockaddr_in*)addr)->sin_addr, ipStr, sizeof(ipStr));
        } else if (addr->sa_family == AF_INET6) {
            inet_ntop(AF_INET6, &((struct sockaddr_in6*)addr)->sin6_addr, ipStr, sizeof(ipStr));
        }
        peerIp_ = ipStr;
    }

    ThreadCtx& threadCtx_;
    std::string dest_;
    int port_;
    EncryptionParams encryptionParams_;
    int64_t ivChangeInterval_;
    std::unique_ptr<WdtSocket> socket_;
    std::string peerIp_;
};
