            if (remaining <= std::chrono::nanoseconds(0)) return false;

            timeval tv;
            tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(remaining).count();
            tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(remaining - std::chrono::seconds(tv.tv_sec)).count();

            int select_result = select(main_socket + 1, nullptr, &write_fds, nullptr, &tv);
            if (select_result <= 0) return false;

            ssize_t bytes_written = write(main_socket, static_cast<const char*>(buffer) + total_written, size - total_written);
            if (bytes_written <= 0) return false;
            total_written += bytes_written;
        }
        return true;
    }

    void Disconnect() {
        if (connected && main_socket != -1) {
            #ifdef _WIN32
            closesocket(main_socket);
            #else
            close(main_socket);
            #endif
            main_socket = -1;
            connected = false;
        }
    }

    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
        #ifdef _WIN32
        u_long mode = nonBlocking ? 1 : 0;
        return ioctlsocket(sockFD, FIONBIO, &mode) == 0;
        #else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) return false;
        flags = nonBlocking ? (flags | O_NONBLOCK) : (flags & ~O_NONBLOCK);
        return fcntl(sockFD, F_SETFL, flags) == 0;
        #endif
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        #ifdef _WIN32
        if (!SetNonBlock(sockFD, true)) return false;

        int connect_result = connect(sockFD, static_cast<sockaddr*>(server_addr), static_cast<int>(server_addr_len));
        if (connect_result != SOCKET_ERROR && WSAGetLastError() != WSAEWOULDBLOCK) {
            return true;
        }

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);

        timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        int select_result = select(sockFD + 1, nullptr, &write_fds, nullptr, &tv);
        if (select_result <= 0) return false;

        int error = 0;
        socklen_t error_len = sizeof(error);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, reinterpret_cast<char*>(&error), &error_len) < 0) {
            return false;
        }

        return error == 0;
        #else
        if (!SetNonBlock(sockFD, true)) return false;

        int connect_result = connect(sockFD, static_cast<sockaddr*>(server_addr), static_cast<socklen_t>(server_addr_len));
        if (connect_result != -1 || (errno != EINPROGRESS && errno != EALREADY)) {
            return connect_result == 0;
        }

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);

        timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        int select_result = select(sockFD + 1, nullptr, &write_fds, nullptr, &tv);
        if (select_result <= 0) return false;

        int error = 0;
        socklen_t error_len = sizeof(error);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, reinterpret_cast<char*>(&error), &error_len) < 0) {
            return false;
        }

        return error == 0;
        #endif
    }

private:
    std::string address;
    uint16_t port;
    SocketHolder main_socket;
    bool connected;
};
