class CurlHttpClient {
public:
    struct MemoryStruct {
        char* memory;
        size_t size;
    };

    static size_t WriteMemoryCallback(void* contents, size_t size, size_t nmemb, void* userp);

    long downloadPage(const std::string& url, std::string& res, const std::vector<std::string>& headers, const std::string& body);
    long getRequest(const std::string& url, json& res, const std::vector<std::string>& headers, const std::string& body);
};

size_t CurlHttpClient::WriteMemoryCallback(void* contents, size_t size, size_t nmemb, void* userp) {
    size_t realsize = size * nmemb;
    MemoryStruct* mem = static_cast<MemoryStruct*>(userp);
    char* ptr = static_cast<char*>(realloc(mem->memory, mem->size + realsize + 1));
    if (ptr) {
        mem->memory = ptr;
        memcpy(&mem->memory[mem->size], contents, realsize);
        mem->size += realsize;
        mem->memory[mem->size] = 0;
    }
    return realsize;
}

long CurlHttpClient::downloadPage(const std::string& url, std::string& res, const std::vector<std::string>& headers, const std::string& body) {
    CURL* curl = curl_easy_init();
    if (!curl) {
        return -1;
    }

    MemoryStruct chunk;
    chunk.memory = static_cast<char*>(malloc(1));
    chunk.size = 0;

    curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
    curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &CurlHttpClient::WriteMemoryCallback);
    curl_easy_setopt(curl, CURLOPT_WRITEDATA, &chunk);

    struct curl_slist* headersList = nullptr;
    for (const auto& header : headers) {
        headersList = curl_slist_append(headersList, header.c_str());
    }
    curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headersList);

    if (!body.empty()) {
        curl_easy_setopt(curl, CURLOPT_POSTFIELDS, body.c_str());
    }

    CURLcode res = curl_easy_perform(curl);
    long httpCode = 0;
    curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &httpCode);

    curl_slist_free_all(headersList);
    curl_easy_cleanup(curl);

    res.assign(chunk.memory, chunk.size);
    free(chunk.memory);

    return httpCode;
}

long CurlHttpClient::getRequest(const std::string& url, json& res, const std::vector<std::string>& headers, const std::string& body) {
    std::string responseStr;
    long httpCode = downloadPage(url, responseStr, headers, body);
    if (httpCode == 200) {
        try {
            res = json::parse(responseStr);
        } catch (...) {
            return -1;
        }
    }
    return httpCode;
}

