typedef struct {
    char* ip;
    unsigned short port;
} stEndPoint;

void* readwrite_routine(void* arg) {
    stEndPoint* endPoint = (stEndPoint*)arg;
    const char* message = "Hello from client";

    while (1) {
        int sockfd = socket(AF_INET, SOCK_STREAM, 0);
        if (sockfd < 0) {
            perror("Socket creation failed");
            sleep(1);
            continue;
        }

        int flags = fcntl(sockfd, F_GETFL, 0);
        fcntl(sockfd, F_SETFL, flags | O_NONBLOCK);

        struct sockaddr_in server_addr;
        memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(endPoint->port);

        if (inet_pton(AF_INET, endPoint->ip, &server_addr.sin_addr) <= 0) {
            perror("Invalid IP address");
            close(sockfd);
            sleep(1);
            continue;
        }

        int connect_ret = connect(sockfd, (struct sockaddr*)&server_addr, sizeof(server_addr));
        if (connect_ret < 0) {
            if (errno == EINPROGRESS) {
                fd_set writefds;
                FD_ZERO(&writefds);
                FD_SET(sockfd, &writefds);

                struct timeval tv;
                tv.tv_sec = 5;
                tv.tv_usec = 0;

                int select_ret = select(sockfd + 1, NULL, &writefds, NULL, &tv);
                if (select_ret > 0) {
                    int so_error;
                    socklen_t len = sizeof(so_error);
                    if (getsockopt(sockfd, SOL_SOCKET, SO_ERROR, &so_error, &len) < 0) {
                        perror("getsockopt failed");
                        close(sockfd);
                        sleep(1);
                        continue;
                    }
                    if (so_error != 0) {
                        perror("Connection failed");
                        close(sockfd);
                        sleep(1);
                        continue;
                    }
                } else {
                    perror("Connection timeout");
                    close(sockfd);
                    sleep(1);
                    continue;
                }
            } else {
                perror("Connect failed");
                close(sockfd);
                sleep(1);
                continue;
            }
        }

        if (send(sockfd, message, strlen(message), 0) < 0) {
            perror("Send failed");
            close(sockfd);
            sleep(1);
            continue;
        }

        char buffer[1024];
        while (1) {
            ssize_t bytes_read = read(sockfd, buffer, sizeof(buffer) - 1);
            if (bytes_read < 0) {
                if (errno == EAGAIN || errno == EWOULDBLOCK) {
                    usleep(100000);
                    continue;
                }
                perror("Read failed");
                close(sockfd);
                break;
            } else if (bytes_read == 0) {
                printf("Connection closed by server\n");
                close(sockfd);
                break;
            } else {
                buffer[bytes_read] = '\0';
                printf("Received: %s\n", buffer);
            }
        }

        sleep(1);
    }

    return NULL;
}
