class HTTPClient {
private:
    std::string baseURL;
public:
    HTTPClient(const std::string& base);
    ~HTTPClient();
    std::string makeRequest(const std::string& endpoint);
    static size_t write_to_string(void* ptr, size_t size, size_t count, void* stream);
};

HTTPClient::HTTPClient(const std::string& base) : baseURL(base) {
    curl_global_init(CURL_GLOBAL_DEFAULT);
}

HTTPClient::~HTTPClient() {
    curl_global_cleanup();
}

std::string HTTPClient::makeRequest(const std::string& endpoint) {
    std::string fullUrl = baseURL + endpoint;
    CURL* curl = curl_easy_init();
    if (!curl) {
        throw std::runtime_error("Failed to initialize CURL");
    }

    std::string response;
    curl_easy_setopt(curl, CURLOPT_URL, fullUrl.c_str());
    curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &HTTPClient::write_to_string);
    curl_easy_setopt(curl, CURLOPT_WRITEDATA, &response);

    CURLcode res = curl_easy_perform(curl);
    if (res != CURLE_OK) {
        throw std::runtime_error("CURL error: " + std::string(curl_easy_strerror(res)));
    }

    curl_easy_cleanup(curl);
    return response;
}

size_t HTTPClient::write_to_string(void* ptr, size_t size, size_t count, void* stream) {
    std::string* response = static_cast<std::string*>(stream);
    size_t realsize = size * count;
    response->append(static_cast<char*>(ptr), realsize);
    return realsize;
}

