}

DownloadTask::~DownloadTask() {
    cleanup();
}

bool DownloadTask::run() {
    if (downloadFile(m_url)) {
        return true;
    }
    m_retries++;
    if (!m_urlBackup.empty() && downloadFile(m_urlBackup)) {
        return true;
    }
    setError("Download failed after retries");
    return false;
}

unsigned int DownloadTask::getRetries() const {
    return m_retries;
}

bool DownloadTask::downloadFile(const std::string& url) {
    CURL* curl = curl_easy_init();
    if (!curl) {
        setError("Failed to initialize CURL");
        return false;
    }

    FILE* fp = fopen(m_outputTmp.c_str(), "wb");
    if (!fp) {
        setError("Failed to open temporary file");
        curl_easy_cleanup(curl);
        return false;
    }

    curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
    curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
    curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &DownloadTask::writeData);
    curl_easy_setopt(curl, CURLOPT_WRITEDATA, fp);

    CURLcode res = curl_easy_perform(curl);
    curl_easy_cleanup(curl);
    fclose(fp);

    if (res != CURLE_OK) {
        setError(std::string("CURL error: ") + curl_easy_strerror(res));
        return false;
    }

    return finalizeDownload();
}

