    if (rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
        setError("Failed to rename temporary file");
        return false;
    }

    struct utimbuf timebuf;
    timebuf.actime = time(nullptr);
    timebuf.modtime = m_mtime;
    if (utime(m_output.c_str(), &timebuf) != 0) {
        setError("Failed to set modification time");
        return false;
    }

    return true;
