class HttpWebResponse {
public:
    HttpWebResponse() {}
    HttpWebResponse(const std::string& content) : content_(content) {}
    std::string content_;
};

class HttpWebRequest {
private:
    std::string uri_;
    std::string method_;
    std::map<std::string, std::string> header_;
    std::iostream is_;

public:
    HttpWebRequest(const std::string& uri) : uri_(uri), method_("GET") {}

    static size_t callback(void* buffer, size_t size, size_t nmemb, void* data) {
        size_t realsize = size * nmemb;
        HttpWebResponse* response = static_cast<HttpWebResponse*>(data);
        response->content_.append(static_cast<char*>(buffer), realsize);
        return realsize;
    }

    HttpWebResponse* GetFileResponse(const char* uri) {
        std::string path = uri;
        if (path.substr(0, 7) == "file://") {
            path = path.substr(7);
        }
        std::ifstream file(path, std::ios::binary | std::ios::ate);
        if (!file) {
            return nullptr;
        }
        std::streampos size = file.tellg();
        file.seekg(0, std::ios::beg);
        std::string content(size, '\0');
        file.read(&content[0], size);
        file.close();
        return new HttpWebResponse(content);
    }

    HttpWebResponse* GetNetResponse(const char* uri) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            return nullptr;
        }

        curl_easy_setopt(curl, CURLOPT_URL, uri);
        curl_easy_setopt(curl, CURLOPT_CUSTOMREQUEST, method_.c_str());

        struct curl_slist* headers = nullptr;
        for (const auto& header : header_) {
            std::string headerStr = header.first + ": " + header.second;
            headers = curl_slist_append(headers, headerStr.c_str());
        }
        curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headers);

        HttpWebResponse* response = new HttpWebResponse();
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &HttpWebRequest::callback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, response);

        CURLcode res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            curl_slist_free_all(headers);
            curl_easy_cleanup(curl);
            delete response;
            return nullptr;
        }

        curl_slist_free_all(headers);
        curl_easy_cleanup(curl);

        return response;
    }

    HttpWebResponse* GetResponse() {
        if (uri_.substr(0, 7) == "file://") {
            return GetFileResponse(uri_.c_str());
        } else {
            return GetNetResponse(uri_.c_str());
        }
    }
};

